detaileddescription.para:
visitor



The visitor that will visit the nodes.




True if all nodes were visited; false if DocumentVisitor stopped the operation before visiting all nodes.

Examples


Shows ways of accessing building blocks in a glossary document. voidGlossaryDocument_()
{
autodoc=MakeObject<Document>();
autoglossaryDoc=MakeObject<GlossaryDocument>();

for(inti=1;i<=5;++i)
{
autoblock=MakeObject<BuildingBlock>(glossaryDoc);
block->set_Name(String(u"Block")+System::Convert::ToString(i));
glossaryDoc->AppendChild(block);
}
ASSERT_EQ(5,glossaryDoc->get_BuildingBlocks()->get_Count());

doc->set_GlossaryDocument(glossaryDoc);

//Therearevariouswaysofaccessingbuildingblocks.
//1-Getthefirst/lastbuildingblocksinthecollection:
ASSERT_EQ(u"Block1",glossaryDoc->get_FirstBuildingBlock()->get_Name());
ASSERT_EQ(u"Block5",glossaryDoc->get_LastBuildingBlock()->get_Name());

//2-Getabuildingblockbyindex:
ASSERT_EQ(u"Block2",glossaryDoc->get_BuildingBlocks()->idx_get(1)->get_Name());
ASSERT_EQ(u"Block3",glossaryDoc->get_BuildingBlocks()->ToArray()->idx_get(2)->get_Name());

//3-Getthefirstbuildingblockthatmatchesagallery,nameandcategory:
ASSERT_EQ(u"Block4",glossaryDoc->GetBuildingBlock(BuildingBlockGallery::All,u"(EmptyCategory)",u"Block4")->get_Name());

//Wewilldothatusingacustomvisitor,
//whichwillgiveeveryBuildingBlockintheGlossaryDocumentauniqueGUID
autovisitor=MakeObject<ExBuildingBlocks::GlossaryDocVisitor>();
glossaryDoc->Accept(visitor);

std::cout<<visitor->GetText()<<std::endl;

//InMicrosoftWord,wecanaccessthebuildingblocksvia"Insert"->"QuickParts"->"BuildingBlocksOrganizer".
doc->Save(ArtifactsDir+u"BuildingBlocks.GlossaryDocument.dotx");
}

classGlossaryDocVisitor:publicDocumentVisitor
{
public:
GlossaryDocVisitor()
{
mBlocksByGuid=MakeObject<System::Collections::Generic::Dictionary<System::Guid,SharedPtr<BuildingBlock>>>();
mBuilder=MakeObject<System::Text::StringBuilder>();
}

StringGetText()
{
returnmBuilder->ToString();
}

SharedPtr<System::Collections::Generic::Dictionary<System::Guid,SharedPtr<BuildingBlock>>>GetDictionary()
{
returnmBlocksByGuid;
}

VisitorActionVisitGlossaryDocumentStart(SharedPtr<GlossaryDocument>glossary)override
{
mBuilder->AppendLine(u"Glossarydocumentfound!");
returnVisitorAction::Continue;
}

VisitorActionVisitGlossaryDocumentEnd(SharedPtr<GlossaryDocument>glossary)override
{
mBuilder->AppendLine(u"Reachedendofglossary!");
mBuilder->AppendLine(String(u"BuildingBlocksfound:")+mBlocksByGuid->get_Count());
returnVisitorAction::Continue;
}

VisitorActionVisitBuildingBlockStart(SharedPtr<BuildingBlock>block)override
{
block->set_Guid(System::Guid::NewGuid());
mBlocksByGuid->Add(block->get_Guid(),block);
returnVisitorAction::Continue;
}

VisitorActionVisitBuildingBlockEnd(SharedPtr<BuildingBlock>block)override
{
mBuilder->AppendLine(String(u"\tVisitedblock\"")+block->get_Name()+u"\"");
mBuilder->AppendLine(String(u"\tType:")+System::ObjectExt::ToString(block->get_Type()));
mBuilder->AppendLine(String(u"\tGallery:")+System::ObjectExt::ToString(block->get_Gallery()));
mBuilder->AppendLine(String(u"\tBehavior:")+System::ObjectExt::ToString(block->get_Behavior()));
mBuilder->AppendLine(String(u"\tDescription:")+block->get_Description());

returnVisitorAction::Continue;
}

private:
SharedPtr<System::Collections::Generic::Dictionary<System::Guid,SharedPtr<BuildingBlock>>>mBlocksByGuid;
SharedPtr<System::Text::StringBuilder>mBuilder;
};

