detaileddescription.para:
doc



The owner document. 




id



The comment identifier to which this object is linked.



Examples


Shows how print the contents of all comments and their comment ranges using a document visitor. voidCreateCommentsAndPrintAllInfo()
{
autodoc=MakeObject<Document>();

autonewComment=MakeObject<Comment>(doc);
newComment->set_Author(u"VDeryushev");
newComment->set_Initial(u"VD");
newComment->set_DateTime(System::DateTime::get_Now());

newComment->SetText(u"Commentregardingtext.");

//Addtexttothedocument,warpitinacommentrange,andthenaddyourcomment.
SharedPtr<Paragraph>para=doc->get_FirstSection()->get_Body()->get_FirstParagraph();
para->AppendChild(MakeObject<CommentRangeStart>(doc,newComment->get_Id()));
para->AppendChild(MakeObject<Run>(doc,u"Commentedtext."));
para->AppendChild(MakeObject<CommentRangeEnd>(doc,newComment->get_Id()));
para->AppendChild(newComment);

//Addtworepliestothecomment.
newComment->AddReply(u"JohnDoe",u"JD",System::DateTime::get_Now(),u"Newreply.");
newComment->AddReply(u"JohnDoe",u"JD",System::DateTime::get_Now(),u"Anotherreply.");

PrintAllCommentInfo(doc->GetChildNodes(NodeType::Comment,true));
}

staticvoidPrintAllCommentInfo(SharedPtr<NodeCollection>comments)
{
autocommentVisitor=MakeObject<ExComment::CommentInfoPrinter>();

//Iterateoveralltop-levelcomments.Unlikereply-typecomments,top-levelcommentshavenoancestor.
std::function<bool(SharedPtr<Node>c)>haveNoAncestor=[](SharedPtr<Node>c)
{
return(System::DynamicCast<Comment>(c))->get_Ancestor()==nullptr;
};

for(autocomment:System::IterateOver<Comment>(comments->LINQ_Where(haveNoAncestor)))
{
//First,visitthestartofthecommentrange.
autocommentRangeStart=System::DynamicCast<CommentRangeStart>(comment->get_PreviousSibling()->get_PreviousSibling()->get_PreviousSibling());
commentRangeStart->Accept(commentVisitor);

//Then,visitthecomment,andanyrepliesthatitmayhave.
comment->Accept(commentVisitor);

for(constauto&reply:System::IterateOver<Comment>(comment->get_Replies()))
{
reply->Accept(commentVisitor);
}

//Finally,visittheendofthecommentrange,andthenprintthevisitor'stextcontents.
autocommentRangeEnd=System::DynamicCast<CommentRangeEnd>(comment->get_PreviousSibling());
commentRangeEnd->Accept(commentVisitor);

std::cout<<commentVisitor->GetText()<<std::endl;
}
}

classCommentInfoPrinter:publicDocumentVisitor
{
public:
CommentInfoPrinter():mVisitorIsInsideComment(false),mDocTraversalDepth(0)
{
mBuilder=MakeObject<System::Text::StringBuilder>();
mVisitorIsInsideComment=false;
}

StringGetText()
{
returnmBuilder->ToString();
}

VisitorActionVisitRun(SharedPtr<Run>run)override
{
if(mVisitorIsInsideComment)
{
IndentAndAppendLine(String(u"[Run]\"")+run->get_Text()+u"\"");
}

returnVisitorAction::Continue;
}

VisitorActionVisitCommentRangeStart(SharedPtr<CommentRangeStart>commentRangeStart)override
{
IndentAndAppendLine(String(u"[Commentrangestart]ID:")+commentRangeStart->get_Id());
mDocTraversalDepth++;
mVisitorIsInsideComment=true;

returnVisitorAction::Continue;
}

VisitorActionVisitCommentRangeEnd(SharedPtr<CommentRangeEnd>commentRangeEnd)override
{
mDocTraversalDepth--;
IndentAndAppendLine(String(u"[Commentrangeend]ID:")+commentRangeEnd->get_Id()+u"\n");
mVisitorIsInsideComment=false;

returnVisitorAction::Continue;
}

VisitorActionVisitCommentStart(SharedPtr<Comment>comment)override
{
IndentAndAppendLine(
String::Format(u"[Commentstart]ForcommentrangeID{0},By{1}on{2}",comment->get_Id(),comment->get_Author(),comment->get_DateTime()));
mDocTraversalDepth++;
mVisitorIsInsideComment=true;

returnVisitorAction::Continue;
}

VisitorActionVisitCommentEnd(SharedPtr<Comment>comment)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Commentend]");
mVisitorIsInsideComment=false;

returnVisitorAction::Continue;
}

private:
boolmVisitorIsInsideComment;
intmDocTraversalDepth;
SharedPtr<System::Text::StringBuilder>mBuilder;

voidIndentAndAppendLine(Stringtext)
{
for(inti=0;i<mDocTraversalDepth;i++)
{
mBuilder->Append(u"|");
}

mBuilder->AppendLine(text);
}
};

visitor



The visitor that will visit the node.




False if the visitor requested the enumeration to stop.

