ASSERT_EQ(u"Initialtext.",paragraph->GetText().Trim());

//Insertthesecondrunintotheparagraphinfrontoftheinitialrun.
paragraph->InsertBefore(run2,paragraphText);

ASSERT_EQ(u"Run2.Initialtext.",paragraph->GetText().Trim());

//Insertthethirdrunaftertheinitialrun.
paragraph->InsertAfter(run3,paragraphText);

ASSERT_EQ(u"Run2.Initialtext.Run3.",paragraph->GetText().Trim());

//Insertthefirstruntothestartoftheparagraph'schildnodescollection.
paragraph->PrependChild(run1);

ASSERT_EQ(u"Run1.Run2.Initialtext.Run3.",paragraph->GetText().Trim());
ASSERT_EQ(4,paragraph->GetChildNodes(NodeType::Any,true)->get_Count());

//Wecanmodifythecontentsoftherunbyeditinganddeletingexistingchildnodes.
(System::DynamicCast<Run>(paragraph->GetChildNodes(NodeType::Run,true)->idx_get(1)))->set_Text(u"Updatedrun2.");
paragraph->GetChildNodes(NodeType::Run,true)->Remove(paragraphText);

ASSERT_EQ(u"Run1.Updatedrun2.Run3.",paragraph->GetText().Trim());
ASSERT_EQ(3,paragraph->GetChildNodes(NodeType::Any,true)->get_Count());

