Shows how to create smart tags. voidCreate()
{
autodoc=MakeObject<Document>();

//AsmarttagappearsinadocumentwithMicrosoftWordrecognizesapartofitstextassomeformofdata,
//suchasaname,date,oraddress,andconvertsittoahyperlinkthatdisplaysapurpledottedunderline.
autosmartTag=MakeObject<SmartTag>(doc);

//Smarttagsarecompositenodesthatcontaintheirrecognizedtextinitsentirety.
//Addcontentstothissmarttagmanually.
smartTag->AppendChild(MakeObject<Run>(doc,u"May29,2019"));

//MicrosoftWordmayrecognizetheabovecontentsasbeingadate.
//Smarttagsusethe"Element"propertytoreflectthetypeofdatatheycontain.
smartTag->set_Element(u"date");

//SomesmarttagtypesprocesstheircontentsfurtherintocustomXMLproperties.
smartTag->get_Properties()->Add(MakeObject<CustomXmlProperty>(u"Day",String::Empty,u"29"));
smartTag->get_Properties()->Add(MakeObject<CustomXmlProperty>(u"Month",String::Empty,u"5"));
smartTag->get_Properties()->Add(MakeObject<CustomXmlProperty>(u"Year",String::Empty,u"2019"));

//Setthesmarttag'sURItothedefaultvalue.
smartTag->set_Uri(u"urn:schemas-microsoft-com:office:smarttags");

doc->get_FirstSection()->get_Body()->get_FirstParagraph()->AppendChild(smartTag);
doc->get_FirstSection()->get_Body()->get_FirstParagraph()->AppendChild(MakeObject<Run>(doc,u"isadate."));

//Createanothersmarttagforastockticker.
smartTag=MakeObject<SmartTag>(doc);
smartTag->set_Element(u"stockticker");
smartTag->set_Uri(u"urn:schemas-microsoft-com:office:smarttags");

smartTag->AppendChild(MakeObject<Run>(doc,u"MSFT"));

doc->get_FirstSection()->get_Body()->get_FirstParagraph()->AppendChild(smartTag);
doc->get_FirstSection()->get_Body()->get_FirstParagraph()->AppendChild(MakeObject<Run>(doc,u"isastockticker."));

//Printallthesmarttagsinourdocumentusingadocumentvisitor.
doc->Accept(MakeObject<ExSmartTag::SmartTagPrinter>());

//OlderversionsofMicrosoftWordsupportsmarttags.
doc->Save(ArtifactsDir+u"SmartTag.Create.doc");

//Usethe"RemoveSmartTags"methodtoremoveallsmarttagsfromadocument.
ASSERT_EQ(2,doc->GetChildNodes(NodeType::SmartTag,true)->get_Count());

doc->RemoveSmartTags();

ASSERT_EQ(0,doc->GetChildNodes(NodeType::SmartTag,true)->get_Count());
}

classSmartTagPrinter:publicDocumentVisitor
{
public:
VisitorActionVisitSmartTagStart(SharedPtr<SmartTag>smartTag)override
{
std::cout<<"Smarttagtype:"<<smartTag->get_Element()<<std::endl;
returnVisitorAction::Continue;
}

VisitorActionVisitSmartTagEnd(SharedPtr<SmartTag>smartTag)override
{
std::cout<<"\tContents:\""<<smartTag->ToString(SaveFormat::Text)<<"\""<<std::endl;

if(smartTag->get_Properties()->get_Count()==0)
{
std::cout<<"\tContainsnoproperties"<<std::endl;
}
else
{
std::cout<<"\tProperties:";
autoproperties=MakeArray<String>(smartTag->get_Properties()->get_Count());
intindex=0;

for(constauto&cxp:System::IterateOver(smartTag->get_Properties()))
{
properties[index++]=String::Format(u"\"{0}\"=\"{1}\"",cxp->get_Name(),cxp->get_Value());
}

std::cout<<String::Join(u",",properties)<<std::endl;
}

returnVisitorAction::Continue;
}
};

Examples


Shows how to traverse through a composite node's collection of child nodes. autodoc=MakeObject<Document>();

//Addtworunsandoneshapeaschildnodestothefirstparagraphofthisdocument.
autoparagraph=System::DynamicCast<Paragraph>(doc->GetChild(NodeType::Paragraph,0,true));
paragraph->AppendChild(MakeObject<Run>(doc,u"Helloworld!"));

autoshape=MakeObject<Shape>(doc,ShapeType::Rectangle);
shape->set_Width(200);
shape->set_Height(200);
//Notethatthe'CustomNodeId'isnotsavedtoanoutputfileandexistsonlyduringthenodelifetime.
shape->set_CustomNodeId(100);
shape->set_WrapType(WrapType::Inline);
paragraph->AppendChild(shape);

paragraph->AppendChild(MakeObject<Run>(doc,u"Helloagain!"));

//Iteratethroughtheparagraph'scollectionofimmediatechildren,
//andprintanyrunsorshapesthatwefindwithin.
SharedPtr<NodeCollection>children=paragraph->get_ChildNodes();

ASSERT_EQ(3,paragraph->get_ChildNodes()->get_Count());

for(constauto&child:System::IterateOver(children))
{
switch(child->get_NodeType())
{
caseNodeType::Run:
std::cout<<"Runcontents:"<<std::endl;
std::cout<<"\t\""<<child->GetText().Trim()<<"\""<<std::endl;
break;

caseNodeType::Shape:{
autochildShape=System::DynamicCast<Shape>(child);
std::cout<<"Shape:"<<std::endl;
std::cout<<String::Format(u"\t{0},{1}x{2}",childShape->get_ShapeType(),childShape->get_Width(),childShape->get_Height())<<std::endl;
ASSERT_EQ(100,shape->get_CustomNodeId());
break;
}

default:
break;
}
