bookmarkEnd



The object that is being visited.




A VisitorAction value that specifies how to continue the enumeration.

Examples


Shows how to add bookmarks and update their contents. voidCreateUpdateAndPrintBookmarks()
{
//Createadocumentwiththreebookmarks,thenuseacustomdocumentvisitorimplementationtoprinttheircontents.
SharedPtr<Document>doc=CreateDocumentWithBookmarks(3);
SharedPtr<BookmarkCollection>bookmarks=doc->get_Range()->get_Bookmarks();

PrintAllBookmarkInfo(bookmarks);

//Bookmarkscanbeaccessedinthebookmarkcollectionbyindexorname,andtheirnamescanbeupdated.
bookmarks->idx_get(0)->set_Name(String::Format(u"{0}_NewName",bookmarks->idx_get(0)->get_Name()));
bookmarks->idx_get(u"MyBookmark_2")->set_Text(String::Format(u"Updatedtextcontentsof{0}",bookmarks->idx_get(1)->get_Name()));

//Printallbookmarksagaintoseeupdatedvalues.
PrintAllBookmarkInfo(bookmarks);
}

staticSharedPtr<Document>CreateDocumentWithBookmarks(intnumberOfBookmarks)
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

for(inti=1;i<=numberOfBookmarks;i++)
{
StringbookmarkName=String(u"MyBookmark_")+i;

builder->Write(u"Textbeforebookmark.");
builder->StartBookmark(bookmarkName);
builder->Write(String::Format(u"Textinside{0}.",bookmarkName));
builder->EndBookmark(bookmarkName);
builder->Writeln(u"Textafterbookmark.");
}

returndoc;
}

staticvoidPrintAllBookmarkInfo(SharedPtr<BookmarkCollection>bookmarks)
{
autobookmarkVisitor=MakeObject<ExBookmarks::BookmarkInfoPrinter>();

//Geteachbookmarkinthecollectiontoacceptavisitorthatwillprintitscontents.
{
SharedPtr<System::Collections::Generic::IEnumerator<SharedPtr<Bookmark>>>enumerator=bookmarks->GetEnumerator();
while(enumerator->MoveNext())
{
SharedPtr<Bookmark>currentBookmark=enumerator->get_Current();

if(currentBookmark!=nullptr)
{
currentBookmark->get_BookmarkStart()->Accept(bookmarkVisitor);
currentBookmark->get_BookmarkEnd()->Accept(bookmarkVisitor);

std::cout<<currentBookmark->get_BookmarkStart()->GetText()<<std::endl;
}
}
}
}

classBookmarkInfoPrinter:publicDocumentVisitor
{
public:
VisitorActionVisitBookmarkStart(SharedPtr<BookmarkStart>bookmarkStart)override
{
std::cout<<"BookmarkStartname:\""<<bookmarkStart->get_Name()<<"\",Contents:\""<<bookmarkStart->get_Bookmark()->get_Text()<<"\""
<<std::endl;
returnVisitorAction::Continue;
}

VisitorActionVisitBookmarkEnd(SharedPtr<BookmarkEnd>bookmarkEnd)override
{
std::cout<<"BookmarkEndname:\""<<bookmarkEnd->get_Name()<<"\""<<std::endl;
returnVisitorAction::Continue;
}
};

