classDocStructurePrinter:publicDocumentVisitor
{
public:
DocStructurePrinter():mDocTraversalDepth(0)
{
mAcceptingNodeChildTree=MakeObject<System::Text::StringBuilder>();
}

StringGetText()
{
returnmAcceptingNodeChildTree->ToString();
}

VisitorActionVisitDocumentStart(SharedPtr<Document>doc)override
{
intchildNodeCount=doc->GetChildNodes(NodeType::Any,true)->get_Count();

IndentAndAppendLine(String(u"[Documentstart]Childnodes:")+childNodeCount);
mDocTraversalDepth++;

//Allowthevisitortocontinuevisitingothernodes.
returnVisitorAction::Continue;
}

VisitorActionVisitDocumentEnd(SharedPtr<Document>doc)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Documentend]");

returnVisitorAction::Continue;
}

VisitorActionVisitSectionStart(SharedPtr<Section>section)override
{
//Gettheindexofoursectionwithinthedocument.
SharedPtr<NodeCollection>docSections=section->get_Document()->GetChildNodes(NodeType::Section,false);
intsectionIndex=docSections->IndexOf(section);

IndentAndAppendLine(String(u"[Sectionstart]Sectionindex:")+sectionIndex);
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitSectionEnd(SharedPtr<Section>section)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Sectionend]");

returnVisitorAction::Continue;
}

VisitorActionVisitBodyStart(SharedPtr<Body>body)override
{
intparagraphCount=body->get_Paragraphs()->get_Count();
IndentAndAppendLine(String(u"[Bodystart]Paragraphs:")+paragraphCount);
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitBodyEnd(SharedPtr<Body>body)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Bodyend]");

returnVisitorAction::Continue;
}

VisitorActionVisitParagraphStart(SharedPtr<Paragraph>paragraph)override
{
IndentAndAppendLine(u"[Paragraphstart]");
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitParagraphEnd(SharedPtr<Paragraph>paragraph)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Paragraphend]");

returnVisitorAction::Continue;
}

VisitorActionVisitRun(SharedPtr<Run>run)override
{
IndentAndAppendLine(String(u"[Run]\"")+run->GetText()+u"\"");

returnVisitorAction::Continue;
}

VisitorActionVisitSubDocument(SharedPtr<SubDocument>subDocument)override
{
IndentAndAppendLine(u"[SubDocument]");

returnVisitorAction::Continue;
}

private:
intmDocTraversalDepth;
SharedPtr<System::Text::StringBuilder>mAcceptingNodeChildTree;

voidIndentAndAppendLine(Stringtext)
{
for(inti=0;i<mDocTraversalDepth;i++)
{
mAcceptingNodeChildTree->Append(u"|");
}

mAcceptingNodeChildTree->AppendLine(text);
}
};

Shows how to use a DocumentVisitor implementation to remove all hidden content from a document. voidRemoveHiddenContentFromDocument()
{
autodoc=MakeObject<Document>(MyDir+u"Hiddencontent.docx");

autohiddenContentRemover=MakeObject<ExFont::RemoveHiddenContentVisitor>();

//Belowarethreetypesoffieldswhichcanacceptadocumentvisitor,
//whichwillallowittovisittheacceptingnode,andthentraverseitschildnodesinadepth-firstmanner.
//1-Paragraphnode:
autopara=System::DynamicCast<Paragraph>(doc->GetChild(NodeType::Paragraph,4,true));
para->Accept(hiddenContentRemover);

//2-Tablenode:
SharedPtr<Table>table=doc->get_FirstSection()->get_Body()->get_Tables()->idx_get(0);
table->Accept(hiddenContentRemover);

//3-Documentnode:
doc->Accept(hiddenContentRemover);

doc->Save(ArtifactsDir+u"Font.RemoveHiddenContentFromDocument.docx");
}

classRemoveHiddenContentVisitor:publicDocumentVisitor
{
public:
VisitorActionVisitFieldStart(SharedPtr<FieldStart>fieldStart)override
{
if(fieldStart->get_Font()->get_Hidden())
{
fieldStart->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitFieldEnd(SharedPtr<FieldEnd>fieldEnd)override
{
if(fieldEnd->get_Font()->get_Hidden())
{
fieldEnd->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitFieldSeparator(SharedPtr<FieldSeparator>fieldSeparator)override
{
if(fieldSeparator->get_Font()->get_Hidden())
{
fieldSeparator->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitRun(SharedPtr<Run>run)override
{
if(run->get_Font()->get_Hidden())
{
run->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitParagraphStart(SharedPtr<Paragraph>paragraph)override
{
if(paragraph->get_ParagraphBreakFont()->get_Hidden())
{
paragraph->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitFormField(SharedPtr<FormField>formField)override
{
if(formField->get_Font()->get_Hidden())
{
formField->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitGroupShapeStart(SharedPtr<GroupShape>groupShape)override
{
if(groupShape->get_Font()->get_Hidden())
{
groupShape->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitShapeStart(SharedPtr<Shape>shape)override
{
if(shape->get_Font()->get_Hidden())
{
shape->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitCommentStart(SharedPtr<Comment>comment)override
{
if(comment->get_Font()->get_Hidden())
{
comment->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitFootnoteStart(SharedPtr<Footnote>footnote)override
{
if(footnote->get_Font()->get_Hidden())
{
footnote->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitSpecialChar(SharedPtr<SpecialChar>specialChar)override
{
if(specialChar->get_Font()->get_Hidden())
{
specialChar->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitTableEnd(SharedPtr<Table>table)override
{
//Thecontentinsidetablecellsmayhavethehiddencontentflag,butthetablesthemselvescannot.
//Ifthistablehadnothingbuthiddencontent,thisvisitorwouldhaveremovedallofit,
//andtherewouldbenochildnodesleft.
//Thus,wecanalsotreatthetableitselfashiddencontentandremoveit.
//Tableswhichareemptybutdonothavehiddencontentwillhavecellswithemptyparagraphsinside,
//whichthisvisitorwillnotremove.
if(!table->get_HasChildNodes())
{
table->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitCellEnd(SharedPtr<Cell>cell)override
{
if(!cell->get_HasChildNodes()&&cell->get_ParentNode()!=nullptr)
{
cell->Remove();
}

returnVisitorAction::Continue;
}

VisitorActionVisitRowEnd(SharedPtr<Row>row)override
{
if(!row->get_HasChildNodes()&&row->get_ParentNode()!=nullptr)
{
row->Remove();
}

returnVisitorAction::Continue;
}
};

row



The object that is being visited.




A VisitorAction value that specifies how to continue the enumeration.

Examples


Shows how to print the node structure of every table in a document. voidTableToText()
{
autodoc=MakeObject<Document>(MyDir+u"DocumentVisitor-compatiblefeatures.docx");
autovisitor=MakeObject<ExDocumentVisitor::TableStructurePrinter>();

//Whenwegetacompositenodetoacceptadocumentvisitor,thevisitorvisitstheacceptingnode,
//andthentraversesallthenode'schildreninadepth-firstmanner.
//Thevisitorcanreadandmodifyeachvisitednode.
doc->Accept(visitor);

std::cout<<visitor->GetText()<<std::endl;
}

classTableStructurePrinter:publicDocumentVisitor
{
public:
TableStructurePrinter():mVisitorIsInsideTable(false),mDocTraversalDepth(0)
{
mVisitedTables=MakeObject<System::Text::StringBuilder>();
mVisitorIsInsideTable=false;
}

StringGetText()
{
returnmVisitedTables->ToString();
}

VisitorActionVisitRun(SharedPtr<Run>run)override
{
if(mVisitorIsInsideTable)
{
IndentAndAppendLine(String(u"[Run]\"")+run->GetText()+u"\"");
}

returnVisitorAction::Continue;
}

VisitorActionVisitTableStart(SharedPtr<Table>table)override
{
introws=0;
intcolumns=0;

if(table->get_Rows()->get_Count()>0)
{
rows=table->get_Rows()->get_Count();
columns=table->get_FirstRow()->get_Count();
}

IndentAndAppendLine(String(u"[Tablestart]Size:")+rows+u"x"+columns);
mDocTraversalDepth++;
mVisitorIsInsideTable=true;

returnVisitorAction::Continue;
}

VisitorActionVisitTableEnd(SharedPtr<Table>table)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Tableend]");
mVisitorIsInsideTable=false;

returnVisitorAction::Continue;
}

VisitorActionVisitRowStart(SharedPtr<Row>row)override
{
StringrowContents=row->GetText().TrimEnd(MakeArray<char16_t>({u'\x0007',u''})).Replace(u"\u0007",u",");
introwWidth=row->IndexOf(row->get_LastCell())+1;
introwIndex=row->get_ParentTable()->IndexOf(row);
StringrowStatusInTable=row->get_IsFirstRow()&&row->get_IsLastRow()?u"only"
:row->get_IsFirstRow()?u"first"
:row->get_IsLastRow()?String(u"last")
:String(u"");
if(rowStatusInTable!=u"")
{
rowStatusInTable=String::Format(u",the{0}rowinthistable,",rowStatusInTable);
}

IndentAndAppendLine(String::Format(u"[Rowstart]Row#{0}{1}width{2},\"{3}\"",++rowIndex,rowStatusInTable,rowWidth,rowContents));
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitRowEnd(SharedPtr<Row>row)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Rowend]");

returnVisitorAction::Continue;
}

VisitorActionVisitCellStart(SharedPtr<Cell>cell)override
{
SharedPtr<Row>row=cell->get_ParentRow();
SharedPtr<Table>table=row->get_ParentTable();
StringcellStatusInRow=cell->get_IsFirstCell()&&cell->get_IsLastCell()?u"only"
:cell->get_IsFirstCell()?u"first"
:cell->get_IsLastCell()?String(u"last")
:String(u"");
if(cellStatusInRow!=u"")
{
cellStatusInRow=String::Format(u",the{0}cellinthisrow",cellStatusInRow);
}

IndentAndAppendLine(String::Format(u"[Cellstart]Row{0},Col{1}{2}",table->IndexOf(row)+1,row->IndexOf(cell)+1,cellStatusInRow));
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitCellEnd(SharedPtr<Cell>cell)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Cellend]");
returnVisitorAction::Continue;
}

private:
boolmVisitorIsInsideTable;
intmDocTraversalDepth;
SharedPtr<System::Text::StringBuilder>mVisitedTables;

voidIndentAndAppendLine(Stringtext)
{
for(inti=0;i<mDocTraversalDepth;i++)
{
mVisitedTables->Append(u"|");
}

mVisitedTables->AppendLine(text);
}
};

