classSmartTagStructurePrinter:publicDocumentVisitor
{
public:
SmartTagStructurePrinter():mVisitorIsInsideSmartTag(false),mDocTraversalDepth(0)
{
mBuilder=MakeObject<System::Text::StringBuilder>();
mVisitorIsInsideSmartTag=false;
}

StringGetText()
{
returnmBuilder->ToString();
}

VisitorActionVisitRun(SharedPtr<Run>run)override
{
if(mVisitorIsInsideSmartTag)
{
IndentAndAppendLine(String(u"[Run]\"")+run->GetText()+u"\"");
}

returnVisitorAction::Continue;
}

VisitorActionVisitSmartTagStart(SharedPtr<SmartTag>smartTag)override
{
IndentAndAppendLine(String(u"[SmartTagstart]Name:")+smartTag->get_Element());
mDocTraversalDepth++;
mVisitorIsInsideSmartTag=true;

returnVisitorAction::Continue;
}

VisitorActionVisitSmartTagEnd(SharedPtr<SmartTag>smartTag)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[SmartTagend]");
mVisitorIsInsideSmartTag=false;

returnVisitorAction::Continue;
}

private:
boolmVisitorIsInsideSmartTag;
intmDocTraversalDepth;
SharedPtr<System::Text::StringBuilder>mBuilder;

voidIndentAndAppendLine(Stringtext)
{
for(inti=0;i<mDocTraversalDepth;i++)
{
mBuilder->Append(u"|");
}

mBuilder->AppendLine(text);
}
};

