Shows how to insert a chart and set a title. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Insertachartshapewithadocumentbuilderandgetitschart.
SharedPtr<Shape>chartShape=builder->InsertChart(ChartType::Bar,400,300);
SharedPtr<Chart>chart=chartShape->get_Chart();

//Usethe"Title"propertytogiveourchartatitle,whichappearsatthetopcenterofthechartarea.
SharedPtr<ChartTitle>title=chart->get_Title();
title->set_Text(u"MyChart");

//Setthe"Show"propertyto"true"tomakethetitlevisible.
title->set_Show(true);

//Setthe"Overlay"propertyto"true"Giveotherchartelementsmoreroombyallowingthemtooverlapthetitle
title->set_Overlay(true);

doc->Save(ArtifactsDir+u"Charts.ChartTitle.docx");

