detaileddescription.para:
Examples


Shows how to use an AUTOTEXTLIST field to select from a list of AutoText entries. voidFieldAutoTextList_()
{
autodoc=MakeObject<Document>();

//Createaglossarydocumentandpopulateitwithautotextentries.
doc->set_GlossaryDocument(MakeObject<GlossaryDocument>());
AppendAutoTextEntry(doc->get_GlossaryDocument(),u"AutoText1",u"ContentsofAutoText1");
AppendAutoTextEntry(doc->get_GlossaryDocument(),u"AutoText2",u"ContentsofAutoText2");
AppendAutoTextEntry(doc->get_GlossaryDocument(),u"AutoText3",u"ContentsofAutoText3");

autobuilder=MakeObject<DocumentBuilder>(doc);

//CreateanAUTOTEXTLISTfieldandsetthetextthatthefieldwilldisplayinMicrosoftWord.
//Setthetexttoprompttheusertoright-clickthisfieldtoselectanAutoTextbuildingblock,
//whosecontentsthefieldwilldisplay.
autofield=System::DynamicCast<FieldAutoTextList>(builder->InsertField(FieldType::FieldAutoTextList,true));
field->set_EntryName(u"RightclickheretoselectanAutoTextblock");
field->set_ListStyle(u"Heading1");
field->set_ScreenTip(u"HovertiptextforAutoTextListgoeshere");

ASSERT_EQ(String(u"AUTOTEXTLIST\"RightclickheretoselectanAutoTextblock\"")+u"\\s\"Heading1\""+
u"\\t\"HovertiptextforAutoTextListgoeshere\"",
field->GetFieldCode());

doc->Save(ArtifactsDir+u"Field.AUTOTEXTLIST.dotx");
}

staticvoidAppendAutoTextEntry(SharedPtr<GlossaryDocument>glossaryDoc,Stringname,Stringcontents)
{
autobuildingBlock=MakeObject<BuildingBlock>(glossaryDoc);
buildingBlock->set_Name(name);
buildingBlock->set_Gallery(BuildingBlockGallery::AutoText);
buildingBlock->set_Category(u"General");
buildingBlock->set_Behavior(BuildingBlockBehavior::Paragraph);

autosection=MakeObject<Section>(glossaryDoc);
section->AppendChild(MakeObject<Body>(glossaryDoc));
section->get_Body()->AppendParagraph(contents);
buildingBlock->AppendChild(section);

glossaryDoc->AppendChild(buildingBlock);
}

