detaileddescription.para:
Examples


Shows how to use the FILLIN field to prompt the user for a response. voidFieldFillIn_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//InsertaFILLINfield.WhenwemanuallyupdatethisfieldinMicrosoftWord,
//itwillpromptustoenteraresponse.Thefieldwillthendisplaytheresponseastext.
autofield=System::DynamicCast<FieldFillIn>(builder->InsertField(FieldType::FieldFillIn,true));
field->set_PromptText(u"Pleaseenteraresponse:");
field->set_DefaultResponse(u"Adefaultresponse.");

//Wecanalsousethesefieldstoasktheuserforauniqueresponseforeachpage
//createdduringamailmergedoneusingMicrosoftWord.
field->set_PromptOnceOnMailMerge(true);

ASSERT_EQ(u"FILLIN\"Pleaseenteraresponse:\"\\d\"Adefaultresponse.\"\\o",field->GetFieldCode());

automergeField=System::DynamicCast<FieldMergeField>(builder->InsertField(FieldType::FieldMergeField,true));
mergeField->set_FieldName(u"MergeField");

//Ifweperformamailmergeprogrammatically,wecanuseacustompromptrespondent
//toautomaticallyeditresponsesforFILLINfieldsthatthemailmergeencounters.
doc->get_FieldOptions()->set_UserPromptRespondent(MakeObject<ExField::PromptRespondent>());
doc->get_MailMerge()->Execute(MakeArray<String>({u"MergeField"}),MakeArray<SharedPtr<System::Object>>({System::ObjectExt::Box<String>(u"")}));

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.FILLIN.docx");
}

classPromptRespondent:publicIFieldUserPromptRespondent
{
public:
StringRespond(StringpromptText,StringdefaultResponse)override
{
returnString(u"ResponsemodifiedbyPromptRespondent.")+defaultResponse;
}
};

