Shows how to insert a TOC, and populate it with entries based on heading styles. voidFieldToc_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->StartBookmark(u"MyBookmark");

//InsertaTOCfield,whichwillcompileallheadingsintoatableofcontents.
//Foreachheading,thisfieldwillcreatealinewiththetextinthatheadingstyletotheleft,
//andthepagetheheadingappearsontotheright.
autofield=System::DynamicCast<FieldToc>(builder->InsertField(FieldType::FieldTOC,true));

//UsetheBookmarkNamepropertytoonlylistheadings
//thatappearwithintheboundsofabookmarkwiththe"MyBookmark"name.
field->set_BookmarkName(u"MyBookmark");

//Textwithabuilt-inheadingstyle,suchas"Heading1",appliedtoitwillcountasaheading.
//WecannameadditionalstylestobepickedupasheadingsbytheTOCinthispropertyandtheirTOClevels.
field->set_CustomStyles(u"Quote;6;IntenseQuote;7");

//Bydefault,Styles/TOClevelsareseparatedintheCustomStylespropertybyacomma,
//butwecansetacustomdelimiterinthisproperty.
doc->get_FieldOptions()->set_CustomTocStyleSeparator(u";");

//ConfigurethefieldtoexcludeanyheadingsthathaveTOClevelsoutsideofthisrange.
field->set_HeadingLevelRange(u"1-3");

//TheTOCwillnotdisplaythepagenumbersofheadingswhoseTOClevelsarewithinthisrange.
field->set_PageNumberOmittingLevelRange(u"2-5");

//Setacustomstringthatwillseparateeveryheadingfromitspagenumber.
field->set_EntrySeparator(u"-");
field->set_InsertHyperlinks(true);
field->set_HideInWebLayout(false);
field->set_PreserveLineBreaks(true);
field->set_PreserveTabs(true);
field->set_UseParagraphOutlineLevel(false);

InsertNewPageWithHeading(builder,u"Firstentry",u"Heading1");
builder->Writeln(u"Paragraphtext.");
InsertNewPageWithHeading(builder,u"Secondentry",u"Heading1");
InsertNewPageWithHeading(builder,u"Thirdentry",u"Quote");
InsertNewPageWithHeading(builder,u"Fourthentry",u"IntenseQuote");

//Thesetwoheadingswillhavethepagenumbersomittedbecausetheyarewithinthe"2-5"range.
InsertNewPageWithHeading(builder,u"Fifthentry",u"Heading2");
InsertNewPageWithHeading(builder,u"Sixthentry",u"Heading3");

//Thisentrydoesnotappearbecause"Heading4"isoutsideofthe"1-3"rangethatwehavesetearlier.
InsertNewPageWithHeading(builder,u"Seventhentry",u"Heading4");

builder->EndBookmark(u"MyBookmark");
builder->Writeln(u"Paragraphtext.");

//ThisentrydoesnotappearbecauseitisoutsidethebookmarkspecifiedbytheTOC.
InsertNewPageWithHeading(builder,u"Eighthentry",u"Heading1");

ASSERT_EQ(u"TOC\\bMyBookmark\\t\"Quote;6;IntenseQuote;7\"\\o1-3\\n2-5\\p-\\h\\x\\w",field->GetFieldCode());

field->UpdatePageNumbers();
doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.TOC.docx");
}

voidInsertNewPageWithHeading(SharedPtr<DocumentBuilder>builder,StringcaptionText,StringstyleName)
{
builder->InsertBreak(BreakType::PageBreak);
StringoriginalStyle=builder->get_ParagraphFormat()->get_StyleName();
builder->get_ParagraphFormat()->set_Style(builder->get_Document()->get_Styles()->idx_get(styleName));
builder->Writeln(captionText);
builder->get_ParagraphFormat()->set_Style(builder->get_Document()->get_Styles()->idx_get(originalStyle));
}

