//UpdatinganAUTHORfieldthatcontainsavalue
//willapplythatvaluetothe"Author"built-inproperty.
ASSERT_EQ(u"JoeBloggs",doc->get_BuiltInDocumentProperties()->get_Author());

//Changingthisproperty,thenupdatingtheAUTHORfieldwillapplythisvaluetothefield.
doc->get_BuiltInDocumentProperties()->set_Author(u"JohnDoe");
field->Update();

ASSERT_EQ(u"AUTHOR",field->GetFieldCode());
ASSERT_EQ(u"JohnDoe",field->get_Result());

//IfweupdateanAUTHORfieldafterchangingits"Name"property,
