field->set_AuthorName(u"JaneDoe");
field->Update();

ASSERT_EQ(u"AUTHOR\"JaneDoe\"",field->GetFieldCode());
ASSERT_EQ(u"JaneDoe",field->get_Result());

//AUTHORfieldsdonotaffecttheDefaultDocumentAuthorproperty.
ASSERT_EQ(u"JaneDoe",doc->get_BuiltInDocumentProperties()->get_Author());
ASSERT_EQ(u"JoeBloggs",doc->get_FieldOptions()->get_DefaultDocumentAuthor());

doc->Save(ArtifactsDir+u"Field.AUTHOR.docx");

