classFieldUpdateCultureProvider:publicIFieldUpdateCultureProvider
{
public:
SharedPtr<System::Globalization::CultureInfo>GetCulture(Stringname,SharedPtr<Field>field)override
{
if(name==u"ru-RU")
{
autoculture=MakeObject<System::Globalization::CultureInfo>(name,false);
SharedPtr<System::Globalization::DateTimeFormatInfo>format=culture->get_DateTimeFormat();
format->set_MonthNames(MakeArray<String>({u"1",u"2",u"3",u"4",u"5",u"6",u"7",u"8",
u"9",u"10",u"11",u"12",u""}));
format->set_MonthGenitiveNames(format->get_MonthNames());
format->set_AbbreviatedMonthNames(MakeArray<String>(
{u"1",u"2",u"3",u"4",u"5",u"6",u"7",u"8",u"9",u"10",u"11",u"12",u""}));
format->set_AbbreviatedMonthGenitiveNames(format->get_AbbreviatedMonthNames());
format->set_DayNames(MakeArray<String>(
{u"7",u"1",u"2",u"3",u"4",u"5",u"6"}));
format->set_AbbreviatedDayNames(MakeArray<String>({u"7",u"1",u"2",u"3",u"4",u"5",u"6"}));
format->set_ShortestDayNames(MakeArray<String>({u"7",u"1",u"2",u"3",u"4",u"5",u"6"}));
format->set_AMDesignator(u"");
format->set_PMDesignator(u"");
constStringpattern=u"yyyyMM(MMMM)dd(dddd)hh:mm:sstt";
format->set_LongDatePattern(pattern);
format->set_LongTimePattern(pattern);
format->set_ShortDatePattern(pattern);
format->set_ShortTimePattern(pattern);
returnculture;
}
elseif(name==u"en-US")
{
returnMakeObject<System::Globalization::CultureInfo>(name,false);
}
else
{
returnnullptr;
}
}
};

