Examples


Shows how to use STYLEREF fields. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//CreatealistbasedusingaMicrosoftWordlisttemplate.
SharedPtr<Aspose::Words::Lists::List>list=doc->get_Lists()->Add(Lists::ListTemplate::NumberDefault);

//Thisgeneratedlistwilldisplay"1.a)".
//Spacebeforethebracketisanon-delimitercharacter,whichwecansuppress.
list->get_ListLevels()->idx_get(0)->set_NumberFormat(u"\x0000"
u".");
list->get_ListLevels()->idx_get(1)->set_NumberFormat(u"\x0001"
u")");

//AddtextandapplyparagraphstylesthatSTYLEREFfieldswillreference.
builder->get_ListFormat()->set_List(list);
builder->get_ListFormat()->ListIndent();
builder->get_ParagraphFormat()->set_Style(doc->get_Styles()->idx_get(u"ListParagraph"));
builder->Writeln(u"Item1");
builder->get_ParagraphFormat()->set_Style(doc->get_Styles()->idx_get(u"Quote"));
builder->Writeln(u"Item2");
builder->get_ParagraphFormat()->set_Style(doc->get_Styles()->idx_get(u"ListParagraph"));
builder->Writeln(u"Item3");
builder->get_ListFormat()->RemoveNumbers();
builder->get_ParagraphFormat()->set_Style(doc->get_Styles()->idx_get(u"Normal"));

//PlaceaSTYLEREFfieldintheheaderanddisplaythefirst"ListParagraph"-styledtextinthedocument.
builder->MoveToHeaderFooter(HeaderFooterType::HeaderPrimary);
autofield=System::DynamicCast<FieldStyleRef>(builder->InsertField(FieldType::FieldStyleRef,true));
field->set_StyleName(u"ListParagraph");

//PlaceaSTYLEREFfieldinthefooter,andhaveitdisplaythelasttext.
builder->MoveToHeaderFooter(HeaderFooterType::FooterPrimary);
field=System::DynamicCast<FieldStyleRef>(builder->InsertField(FieldType::FieldStyleRef,true));
field->set_StyleName(u"ListParagraph");
field->set_SearchFromBottom(true);

builder->MoveToDocumentEnd();

//WecanalsouseSTYLEREFfieldstoreferencethelistnumbersoflists.
builder->Write(u"\nParagraphnumber:");
field=System::DynamicCast<FieldStyleRef>(builder->InsertField(FieldType::FieldStyleRef,true));
field->set_StyleName(u"Quote");
field->set_InsertParagraphNumber(true);

builder->Write(u"\nParagraphnumber,relativecontext:");
field=System::DynamicCast<FieldStyleRef>(builder->InsertField(FieldType::FieldStyleRef,true));
field->set_StyleName(u"Quote");
field->set_InsertParagraphNumberInRelativeContext(true);

builder->Write(u"\nParagraphnumber,fullcontext:");
field=System::DynamicCast<FieldStyleRef>(builder->InsertField(FieldType::FieldStyleRef,true));
field->set_StyleName(u"Quote");
field->set_InsertParagraphNumberInFullContext(true);

builder->Write(u"\nParagraphnumber,fullcontext,non-delimitercharssuppressed:");
field=System::DynamicCast<FieldStyleRef>(builder->InsertField(FieldType::FieldStyleRef,true));
field->set_StyleName(u"Quote");
field->set_InsertParagraphNumberInFullContext(true);
field->set_SuppressNonDelimiters(true);

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.STYLEREF.docx");

