detaileddescription.para:
Examples


Shows how to insert a TOC field, and filter which TC fields end up as entries. voidFieldTocEntryIdentifier()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//InsertaTOCfield,whichwillcompileallTCfieldsintoatableofcontents.
autofieldToc=System::DynamicCast<FieldToc>(builder->InsertField(FieldType::FieldTOC,true));

//ConfigurethefieldonlytopickupTCentriesofthe"A"type,andanentry-levelbetween1and3.
fieldToc->set_EntryIdentifier(u"A");
fieldToc->set_EntryLevelRange(u"1-3");

ASSERT_EQ(u"TOC\\fA\\l1-3",fieldToc->GetFieldCode());

//Thesetwoentrieswillappearinthetable.
builder->InsertBreak(BreakType::PageBreak);
InsertTocEntry(builder,u"TCfield1",u"A",u"1");
InsertTocEntry(builder,u"TCfield2",u"A",u"2");

ASSERT_EQ(u"TC\"TCfield1\"\\n\\fA\\l1",doc->get_Range()->get_Fields()->idx_get(1)->GetFieldCode());

//Thisentrywillbeomittedfromthetablebecauseithasadifferenttypefrom"A".
InsertTocEntry(builder,u"TCfield3",u"B",u"1");

//Thisentrywillbeomittedfromthetablebecauseithasanentry-leveloutsideofthe1-3range.
InsertTocEntry(builder,u"TCfield4",u"A",u"5");

doc->UpdateFields();
doc->Save(ArtifactsDir+u"Field.TC.docx");
}

voidInsertTocEntry(SharedPtr<DocumentBuilder>builder,Stringtext,StringtypeIdentifier,StringentryLevel)
{
autofieldTc=System::DynamicCast<FieldTC>(builder->InsertField(FieldType::FieldTOCEntry,true));
fieldTc->set_OmitPageNumber(true);
fieldTc->set_Text(text);
fieldTc->set_TypeIdentifier(typeIdentifier);
fieldTc->set_EntryLevel(entryLevel);
}

