Aspose::Words::Lists::List::get_Style 


Aspose::Words::Lists::List::get_IsListStyleReference

Examples


Shows how to create a list style and use it in a document. autodoc=MakeObject<Document>();

//Alistallowsustoorganizeanddecoratesetsofparagraphswithprefixsymbolsandindents.
//Wecancreatenestedlistsbyincreasingtheindentlevel.
//Wecanbeginandendalistbyusingadocumentbuilder's"ListFormat"property.
//Eachparagraphthatweaddbetweenalist'sstartandtheendwillbecomeaniteminthelist.
//WecancontainanentireListobjectwithinastyle.
SharedPtr<Style>listStyle=doc->get_Styles()->Add(StyleType::List,u"MyListStyle");

SharedPtr<List>list1=listStyle->get_List();

ASSERT_TRUE(list1->get_IsListStyleDefinition());
ASSERT_FALSE(list1->get_IsListStyleReference());
ASSERT_TRUE(list1->get_IsMultiLevel());
ASPOSE_ASSERT_EQ(listStyle,list1->get_Style());

//Changetheappearanceofalllistlevelsinourlist.
for(constauto&level:list1->get_ListLevels())
{
level->get_Font()->set_Name(u"Verdana");
level->get_Font()->set_Color(System::Drawing::Color::get_Blue());
level->get_Font()->set_Bold(true);
}

autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"Usingliststylefirsttime:");

//Createanotherlistfromalistwithinastyle.
SharedPtr<List>list2=doc->get_Lists()->Add(listStyle);

ASSERT_FALSE(list2->get_IsListStyleDefinition());
ASSERT_TRUE(list2->get_IsListStyleReference());
ASPOSE_ASSERT_EQ(listStyle,list2->get_Style());

//Addsomelistitemsthatourlistwillformat.
builder->get_ListFormat()->set_List(list2);
builder->Writeln(u"Item1");
builder->Writeln(u"Item2");
builder->get_ListFormat()->RemoveNumbers();

builder->Writeln(u"Usingliststylesecondtime:");

//Createandapplyanotherlistbasedontheliststyle.
SharedPtr<List>list3=doc->get_Lists()->Add(listStyle);
builder->get_ListFormat()->set_List(list3);
builder->Writeln(u"Item1");
builder->Writeln(u"Item2");
builder->get_ListFormat()->RemoveNumbers();

builder->get_Document()->Save(ArtifactsDir+u"Lists.CreateAndUseListStyle.docx");

