Shows how to output all paragraphs in a document that are list items. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->get_ListFormat()->ApplyNumberDefault();
builder->Writeln(u"Numberedlistitem1");
builder->Writeln(u"Numberedlistitem2");
builder->Writeln(u"Numberedlistitem3");
builder->get_ListFormat()->RemoveNumbers();

builder->get_ListFormat()->ApplyBulletDefault();
builder->Writeln(u"Bulletedlistitem1");
builder->Writeln(u"Bulletedlistitem2");
builder->Writeln(u"Bulletedlistitem3");
builder->get_ListFormat()->RemoveNumbers();

SharedPtr<NodeCollection>paras=doc->GetChildNodes(NodeType::Paragraph,true);

for(autopara:System::IterateOver(
paras->LINQ_OfType<SharedPtr<Paragraph>>()->LINQ_Where([](SharedPtr<Paragraph>p){returnp->get_ListFormat()->get_IsListItem();})))
{
std::cout<<String::Format(u"ThisparagraphbelongstolistID#{0},numberstyle\"{1}\"",para->get_ListFormat()->get_List()->get_ListId(),
para->get_ListFormat()->get_ListLevel()->get_NumberStyle())
<<std::endl;
std::cout<<"\t\""<<para->GetText().Trim()<<"\""<<std::endl;
}

