Shows how to restart numbering in a list by copying a list. autodoc=MakeObject<Document>();

//Alistallowsustoorganizeanddecoratesetsofparagraphswithprefixsymbolsandindents.
//Wecancreatenestedlistsbyincreasingtheindentlevel.
//Wecanbeginandendalistbyusingadocumentbuilder's"ListFormat"property.
//Eachparagraphthatweaddbetweenalist'sstartandtheendwillbecomeaniteminthelist.
//CreatealistfromaMicrosoftWordtemplate,andcustomizeitsfirstlistlevel.
SharedPtr<List>list1=doc->get_Lists()->Add(ListTemplate::NumberArabicParenthesis);
list1->get_ListLevels()->idx_get(0)->get_Font()->set_Color(System::Drawing::Color::get_Red());
list1->get_ListLevels()->idx_get(0)->set_Alignment(ListLevelAlignment::Right);

//Applyourlisttosomeparagraphs.
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"List1startsbelow:");
builder->get_ListFormat()->set_List(list1);
builder->Writeln(u"Item1");
builder->Writeln(u"Item2");
builder->get_ListFormat()->RemoveNumbers();

//Wecanaddacopyofanexistinglisttothedocument'slistcollection
//tocreateasimilarlistwithoutmakingchangestotheoriginal.
SharedPtr<List>list2=doc->get_Lists()->AddCopy(list1);
list2->get_ListLevels()->idx_get(0)->get_Font()->set_Color(System::Drawing::Color::get_Blue());
list2->get_ListLevels()->idx_get(0)->set_StartAt(10);

//Applythesecondlisttonewparagraphs.
builder->Writeln(u"List2startsbelow:");
builder->get_ListFormat()->set_List(list2);
builder->Writeln(u"Item1");
builder->Writeln(u"Item2");
builder->get_ListFormat()->RemoveNumbers();

doc->Save(ArtifactsDir+u"Lists.RestartNumberingUsingListCopy.docx");
