Shows how to set a custom image icon for list item labels. autodoc=MakeObject<Document>();

SharedPtr<List>list=doc->get_Lists()->Add(ListTemplate::BulletCircle);

//Createapicturebulletforthecurrentlistlevel,andsetanimagefromalocalfilesystem
//astheiconthatthebulletsforthislistlevelwilldisplay.
list->get_ListLevels()->idx_get(0)->CreatePictureBullet();
list->get_ListLevels()->idx_get(0)->get_ImageData()->SetImage(ImageDir+u"Logoicon.ico");

ASSERT_TRUE(list->get_ListLevels()->idx_get(0)->get_ImageData()->get_HasImage());

autobuilder=MakeObject<DocumentBuilder>(doc);

builder->get_ListFormat()->set_List(list);
builder->Writeln(u"Helloworld!");
builder->Write(u"Helloagain!");

doc->Save(ArtifactsDir+u"Lists.CreatePictureBullet.docx");

list->get_ListLevels()->idx_get(0)->DeletePictureBullet();

ASSERT_TRUE(list->get_ListLevels()->idx_get(0)->get_ImageData()==nullptr);

