Examples


Shows advances ways of customizing list labels. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Alistallowsustoorganizeanddecoratesetsofparagraphswithprefixsymbolsandindents.
//Wecancreatenestedlistsbyincreasingtheindentlevel.
//Wecanbeginandendalistbyusingadocumentbuilder's"ListFormat"property.
//Eachparagraphthatweaddbetweenalist'sstartandtheendwillbecomeaniteminthelist.
SharedPtr<List>list=doc->get_Lists()->Add(ListTemplate::NumberDefault);

//Level1labelswillbeformattedaccordingtothe"Heading1"paragraphstyleandwillhaveaprefix.
//Thesewilllooklike"AppendixA","AppendixB"...
list->get_ListLevels()->idx_get(0)->set_NumberFormat(u"Appendix\x0000");
list->get_ListLevels()->idx_get(0)->set_NumberStyle(NumberStyle::UppercaseLetter);
list->get_ListLevels()->idx_get(0)->set_LinkedStyle(doc->get_Styles()->idx_get(u"Heading1"));

//Level2labelswilldisplaythecurrentnumbersofthefirstandthesecondlistlevelsandhaveleadingzeroes.
//Ifthefirstlistlevelisat1,thenthelistlabelsfromthesewilllooklike"Section(1.01)","Section(1.02)"...
list->get_ListLevels()->idx_get(1)->set_NumberFormat(u"Section(\x0000"
u".\x0001"
u")");
list->get_ListLevels()->idx_get(1)->set_NumberStyle(NumberStyle::LeadingZero);

//Notethatthehigher-levelusesUppercaseLetternumbering.
//Wecansetthe"IsLegal"propertytouseArabicnumbersforthehigherlistlevels.
list->get_ListLevels()->idx_get(1)->set_IsLegal(true);
list->get_ListLevels()->idx_get(1)->set_RestartAfterLevel(0);

//Level3labelswillbeuppercaseRomannumeralswithaprefixandasuffixandwillrestartateachListlevel1item.
//Theselistlabelswilllooklike"-I-","-II-"...
list->get_ListLevels()->idx_get(2)->set_NumberFormat(u"-\x0002"
u"-");
list->get_ListLevels()->idx_get(2)->set_NumberStyle(NumberStyle::UppercaseRoman);
list->get_ListLevels()->idx_get(2)->set_RestartAfterLevel(1);

//Makelabelsofalllistlevelsbold.
for(constauto&level:list->get_ListLevels())
{
level->get_Font()->set_Bold(true);
}

//Applylistformattingtothecurrentparagraph.
builder->get_ListFormat()->set_List(list);

//Createlistitemsthatwilldisplayallthreeofourlistlevels.
for(intn=0;n<2;n++)
{
for(inti=0;i<3;i++)
{
builder->get_ListFormat()->set_ListLevelNumber(i);
builder->Writeln(String(u"Level")+i);
}
}

builder->get_ListFormat()->RemoveNumbers();

doc->Save(ArtifactsDir+u"Lists.CreateListRestartAfterHigher.docx");

