Examples


Shows how to set a time limit for web requests when loading a document with external resources linked by URLs. voidWebRequestTimeout()
{
//CreateanewHtmlLoadOptionsobjectandverifyitstimeoutthresholdforawebrequest.
autooptions=MakeObject<HtmlLoadOptions>();

//WhenloadinganHtmldocumentwithresourcesexternallylinkedbyawebaddressURL,
//Aspose.Wordswillabortwebrequeststhatfailtofetchtheresourceswithinthistimelimit,inmilliseconds.
ASSERT_EQ(100000,options->get_WebRequestTimeout());

//SetaWarningCallbackthatwillrecordallwarningsthatoccurduringloading.
autowarningCallback=MakeObject<ExHtmlLoadOptions::ListDocumentWarnings>();
options->set_WarningCallback(warningCallback);

//Loadsuchadocumentandverifythatashapewithimagedatahasbeencreated.
//Thislinkedimagewillrequireawebrequesttoload,whichwillhavetocompletewithinourtimelimit.
Stringhtml=String(u"<html>\n<imgsrc=\"")+ImageUrl+u"\"alt=\"Asposelogo\"style=\"width:400px;height:400px;\">\n</html>";

autodoc=MakeObject<Document>(MakeObject<System::IO::MemoryStream>(System::Text::Encoding::get_UTF8()->GetBytes(html)),options);
autoimageShape=System::DynamicCast<Shape>(doc->GetChild(NodeType::Shape,0,true));

ASSERT_EQ(1109843,imageShape->get_ImageData()->get_ImageBytes()->get_Length());
ASSERT_EQ(0,warningCallback->Warnings()->get_Count());

//Setanunreasonabletimeoutlimitandtryloadthedocumentagain.
options->set_WebRequestTimeout(0);
doc=MakeObject<Document>(MakeObject<System::IO::MemoryStream>(System::Text::Encoding::get_UTF8()->GetBytes(html)),options);

//Awebrequestthatfailstoobtainanimagewithinthetimelimitwillstillproduceanimage.
//However,theimagewillbethered'x'thatcommonlysignifiesmissingimages.
imageShape=System::DynamicCast<Shape>(doc->GetChild(NodeType::Shape,0,true));
ASSERT_EQ(924,imageShape->get_ImageData()->get_ImageBytes()->get_Length());

//Wecanalsoconfigureacustomcallbacktopickupanywarningsfromtimedoutwebrequests.
ASSERT_EQ(WarningSource::Html,warningCallback->Warnings()->idx_get(0)->get_Source());
ASSERT_EQ(WarningType::DataLoss,warningCallback->Warnings()->idx_get(0)->get_WarningType());
ASSERT_EQ(String::Format(u"Couldn'tloadaresourcefrom\'{0}\'.",ImageUrl),warningCallback->Warnings()->idx_get(0)->get_Description());

ASSERT_EQ(WarningSource::Html,warningCallback->Warnings()->idx_get(1)->get_Source());
ASSERT_EQ(WarningType::DataLoss,warningCallback->Warnings()->idx_get(1)->get_WarningType());
ASSERT_EQ(u"Imagehasbeenreplacedwithaplaceholder.",warningCallback->Warnings()->idx_get(1)->get_Description());

doc->Save(ArtifactsDir+u"HtmlLoadOptions.WebRequestTimeout.docx");
}

classListDocumentWarnings:publicIWarningCallback
{
public:
voidWarning(SharedPtr<WarningInfo>info)override
{
mWarnings->Add(info);
}

SharedPtr<System::Collections::Generic::List<SharedPtr<WarningInfo>>>Warnings()
{
returnmWarnings;
}

ListDocumentWarnings():mWarnings(MakeObject<System::Collections::Generic::List<SharedPtr<WarningInfo>>>())
{
}

private:
SharedPtr<System::Collections::Generic::List<SharedPtr<WarningInfo>>>mWarnings;
};
