detaileddescription.para:
Examples


Shows how to measure and scale shapes. autodoc=MakeObject<Document>(MyDir+u"Officemath.docx");

autoofficeMath=System::DynamicCast<OfficeMath>(doc->GetChild(NodeType::OfficeMath,0,true));
autorenderer=MakeObject<OfficeMathRenderer>(officeMath);

//VerifythesizeoftheimagethattheOfficeMathobjectwillcreatewhenwerenderit.
ASSERT_NEAR(119.0f,renderer->get_SizeInPoints().get_Width(),0.2f);
ASSERT_NEAR(13.0f,renderer->get_SizeInPoints().get_Height(),0.1f);

ASSERT_NEAR(119.0f,renderer->get_BoundsInPoints().get_Width(),0.2f);
ASSERT_NEAR(13.0f,renderer->get_BoundsInPoints().get_Height(),0.1f);

//Shapeswithtransparentpartsmaycontaindifferentvaluesinthe"OpaqueBoundsInPoints"properties.
ASSERT_NEAR(119.0f,renderer->get_OpaqueBoundsInPoints().get_Width(),0.2f);
ASSERT_NEAR(14.2f,renderer->get_OpaqueBoundsInPoints().get_Height(),0.1f);

//Gettheshapesizeinpixels,withlinearscalingtoaspecificDPI.
System::Drawing::Rectanglebounds=renderer->GetBoundsInPixels(1.0f,96.0f);

ASSERT_EQ(159,bounds.get_Width());
ASSERT_EQ(18,bounds.get_Height());

//Gettheshapesizeinpixels,butwithadifferentDPIforthehorizontalandverticaldimensions.
bounds=renderer->GetBoundsInPixels(1.0f,96.0f,150.0f);
ASSERT_EQ(159,bounds.get_Width());
ASSERT_EQ(28,bounds.get_Height());

//Theopaqueboundsmayvaryherealso.
bounds=renderer->GetOpaqueBoundsInPixels(1.0f,96.0f);

ASSERT_EQ(159,bounds.get_Width());
ASSERT_EQ(18,bounds.get_Height());

bounds=renderer->GetOpaqueBoundsInPixels(1.0f,96.0f,150.0f);

ASSERT_EQ(159,bounds.get_Width());
ASSERT_EQ(30,bounds.get_Height());

