Examples


Shows how to change metafiles compression in a document while saving. //OpenadocumentthatcontainsaMicrosoftEquation3.0formula.
autodoc=MakeObject<Document>(MyDir+u"Microsoftequationobject.docx");

//Whenwesaveadocument,smallermetafilesarenotcompressedforperformancereasons.
//WecansetaflaginaSaveOptionsobjecttocompresseverymetafilewhensaving.
//SomeeditorssuchasLibreOfficecannotreaduncompressedmetafiles.
autosaveOptions=MakeObject<DocSaveOptions>();
saveOptions->set_AlwaysCompressMetafiles(compressAllMetafiles);

doc->Save(ArtifactsDir+u"DocSaveOptions.AlwaysCompressMetafiles.docx",saveOptions);

if(compressAllMetafiles)
{
ASSERT_LT(10000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"DocSaveOptions.AlwaysCompressMetafiles.docx")->get_Length());
}
else
{
ASSERT_GE(30000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"DocSaveOptions.AlwaysCompressMetafiles.docx")->get_Length());
}

