detaileddescription.para:
doc



The owner document.



Examples


Shows how to construct an Aspose.Words document by hand. autodoc=MakeObject<Document>();

//Ablankdocumentcontainsonesection,onebodyandoneparagraph.
//Callthe"RemoveAllChildren"methodtoremoveallthosenodes,
//andendupwithadocumentnodewithnochildren.
doc->RemoveAllChildren();

//Thisdocumentnowhasnocompositechildnodesthatwecanaddcontentto.
//Ifwewishtoeditit,wewillneedtorepopulateitsnodecollection.
//First,createanewsection,andthenappenditasachildtotherootdocumentnode.
autosection=MakeObject<Section>(doc);
doc->AppendChild(section);

//Setsomepagesetuppropertiesforthesection.
section->get_PageSetup()->set_SectionStart(SectionStart::NewPage);
section->get_PageSetup()->set_PaperSize(PaperSize::Letter);

//Asectionneedsabody,whichwillcontainanddisplayallitscontents
//onthepagebetweenthesection'sheaderandfooter.
autobody=MakeObject<Body>(doc);
section->AppendChild(body);

//Createaparagraph,setsomeformattingproperties,andthenappenditasachildtothebody.
autopara=MakeObject<Paragraph>(doc);

para->get_ParagraphFormat()->set_StyleName(u"Heading1");
para->get_ParagraphFormat()->set_Alignment(ParagraphAlignment::Center);

body->AppendChild(para);

//Finally,addsomecontenttodothedocument.Createarun,
//setitsappearanceandcontents,andthenappenditasachildtotheparagraph.
autorun=MakeObject<Run>(doc);
run->set_Text(u"HelloWorld!");
run->get_Font()->set_Color(System::Drawing::Color::get_Red());
para->AppendChild(run);

ASSERT_EQ(u"HelloWorld!",doc->GetText().Trim());

doc->Save(ArtifactsDir+u"Section.CreateManually.docx");

visitor



The visitor that will visit the nodes.




True if all nodes were visited; false if DocumentVisitor stopped the operation before visiting all nodes.

Examples


Shows how to use a document visitor to print a document's node structure. voidDocStructureToText()
{
autodoc=MakeObject<Document>(MyDir+u"DocumentVisitor-compatiblefeatures.docx");
autovisitor=MakeObject<ExDocumentVisitor::DocStructurePrinter>();

//Whenwegetacompositenodetoacceptadocumentvisitor,thevisitorvisitstheacceptingnode,
//andthentraversesallthenode'schildreninadepth-firstmanner.
//Thevisitorcanreadandmodifyeachvisitednode.
doc->Accept(visitor);

std::cout<<visitor->GetText()<<std::endl;
}

classDocStructurePrinter:publicDocumentVisitor
{
public:
DocStructurePrinter():mDocTraversalDepth(0)
{
mAcceptingNodeChildTree=MakeObject<System::Text::StringBuilder>();
}

StringGetText()
{
returnmAcceptingNodeChildTree->ToString();
}

VisitorActionVisitDocumentStart(SharedPtr<Document>doc)override
{
intchildNodeCount=doc->GetChildNodes(NodeType::Any,true)->get_Count();

IndentAndAppendLine(String(u"[Documentstart]Childnodes:")+childNodeCount);
mDocTraversalDepth++;

//Allowthevisitortocontinuevisitingothernodes.
returnVisitorAction::Continue;
}

VisitorActionVisitDocumentEnd(SharedPtr<Document>doc)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Documentend]");

returnVisitorAction::Continue;
}

VisitorActionVisitSectionStart(SharedPtr<Section>section)override
{
//Gettheindexofoursectionwithinthedocument.
SharedPtr<NodeCollection>docSections=section->get_Document()->GetChildNodes(NodeType::Section,false);
intsectionIndex=docSections->IndexOf(section);

IndentAndAppendLine(String(u"[Sectionstart]Sectionindex:")+sectionIndex);
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitSectionEnd(SharedPtr<Section>section)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Sectionend]");

returnVisitorAction::Continue;
}

VisitorActionVisitBodyStart(SharedPtr<Body>body)override
{
intparagraphCount=body->get_Paragraphs()->get_Count();
IndentAndAppendLine(String(u"[Bodystart]Paragraphs:")+paragraphCount);
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitBodyEnd(SharedPtr<Body>body)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Bodyend]");

returnVisitorAction::Continue;
}

VisitorActionVisitParagraphStart(SharedPtr<Paragraph>paragraph)override
{
IndentAndAppendLine(u"[Paragraphstart]");
mDocTraversalDepth++;

returnVisitorAction::Continue;
}

VisitorActionVisitParagraphEnd(SharedPtr<Paragraph>paragraph)override
{
mDocTraversalDepth--;
IndentAndAppendLine(u"[Paragraphend]");

returnVisitorAction::Continue;
}

VisitorActionVisitRun(SharedPtr<Run>run)override
{
IndentAndAppendLine(String(u"[Run]\"")+run->GetText()+u"\"");

returnVisitorAction::Continue;
}

VisitorActionVisitSubDocument(SharedPtr<SubDocument>subDocument)override
{
IndentAndAppendLine(u"[SubDocument]");

returnVisitorAction::Continue;
}

private:
intmDocTraversalDepth;
SharedPtr<System::Text::StringBuilder>mAcceptingNodeChildTree;

voidIndentAndAppendLine(Stringtext)
{
for(inti=0;i<mDocTraversalDepth;i++)
{
mAcceptingNodeChildTree->Append(u"|");
}

mAcceptingNodeChildTree->AppendLine(text);
}
};

Examples


Clears main text from all sections from the document leaving the sections themselves. autodoc=MakeObject<Document>();

//Ablankdocumentcontainsonesection,onebodyandoneparagraph.
//Callthe"RemoveAllChildren"methodtoremoveallthosenodes,
//andendupwithadocumentnodewithnochildren.
doc->RemoveAllChildren();

//Thisdocumentnowhasnocompositechildnodesthatwecanaddcontentto.
//Ifwewishtoeditit,wewillneedtorepopulateitsnodecollection.
//First,createanewsection,andthenappenditasachildtotherootdocumentnode.
autosection=MakeObject<Section>(doc);
doc->AppendChild(section);

//Asectionneedsabody,whichwillcontainanddisplayallitscontents
//onthepagebetweenthesection'sheaderandfooter.
autobody=MakeObject<Body>(doc);
section->AppendChild(body);

//Thisbodyhasnochildren,sowecannotaddrunstoityet.
ASSERT_EQ(0,doc->get_FirstSection()->get_Body()->GetChildNodes(NodeType::Any,true)->get_Count());

//Callthe"EnsureMinimum"tomakesurethatthisbodycontainsatleastoneemptyparagraph.
body->EnsureMinimum();

//Now,wecanaddrunstothebody,andgetthedocumenttodisplaythem.
body->get_FirstParagraph()->AppendChild(MakeObject<Run>(doc,u"Helloworld!"));

ASSERT_EQ(u"Helloworld!",doc->GetText().Trim());

Examples


Shows how to iterate through the children of a composite node. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Write(u"Section1");
builder->MoveToHeaderFooter(HeaderFooterType::HeaderPrimary);
builder->Write(u"Primaryheader");
builder->MoveToHeaderFooter(HeaderFooterType::FooterPrimary);
builder->Write(u"Primaryfooter");

SharedPtr<Section>section=doc->get_FirstSection();

//ASectionisacompositenodeandcancontainchildnodes,
//butonlyifthosechildnodesareofa"Body"or"HeaderFooter"nodetype.
for(constauto&node:System::IterateOver(section))
{
switch(node->get_NodeType())
{
caseNodeType::Body:{
autobody=System::DynamicCast<Body>(node);

std::cout<<"Body:"<<std::endl;
std::cout<<"\t\""<<body->GetText().Trim()<<"\""<<std::endl;
break;
}

caseNodeType::HeaderFooter:{
autoheaderFooter=System::DynamicCast<HeaderFooter>(node);

std::cout<<String::Format(u"HeaderFootertype:{0}:",headerFooter->get_HeaderFooterType())<<std::endl;
std::cout<<"\t\""<<headerFooter->GetText().Trim()<<"\""<<std::endl;
break;
}

default:{
throwSystem::Exception(u"Unexpectednodetypeinasection.");
}
}
}

Examples


Shows how to store endnotes at the end of each section, and modify their positions. voidSuppressEndnotes()
{
autodoc=MakeObject<Document>();
doc->RemoveAllChildren();

//Bydefault,adocumentcompilesallendnotesatitsend.
ASSERT_EQ(EndnotePosition::EndOfDocument,doc->get_EndnoteOptions()->get_Position());

//Weusethe"Position"propertyofthedocument's"EndnoteOptions"object
//tocollectendnotesattheendofeachsectioninstead.
doc->get_EndnoteOptions()->set_Position(EndnotePosition::EndOfSection);

InsertSectionWithEndnote(doc,u"Section1",u"Endnote1,willstayinsection1");
InsertSectionWithEndnote(doc,u"Section2",u"Endnote2,willbepusheddowntosection3");
InsertSectionWithEndnote(doc,u"Section3",u"Endnote3,willstayinsection3");

//Whilegettingsectionstodisplaytheirrespectiveendnotes,wecansetthe"SuppressEndnotes"flag
//ofasection's"PageSetup"objectto"true"toreverttothedefaultbehaviorandpassitsendnotes
//ontothenextsection.
SharedPtr<PageSetup>pageSetup=doc->get_Sections()->idx_get(1)->get_PageSetup();
pageSetup->set_SuppressEndnotes(true);

doc->Save(ArtifactsDir+u"PageSetup.SuppressEndnotes.docx");
}

staticvoidInsertSectionWithEndnote(SharedPtr<Document>doc,StringsectionBodyText,StringendnoteText)
{
autosection=MakeObject<Section>(doc);

doc->AppendChild(section);

autobody=MakeObject<Body>(doc);
section->AppendChild(body);

ASPOSE_ASSERT_EQ(section,body->get_ParentNode());

autopara=MakeObject<Paragraph>(doc);
body->AppendChild(para);

ASPOSE_ASSERT_EQ(body,para->get_ParentNode());

autobuilder=MakeObject<DocumentBuilder>(doc);
builder->MoveTo(para);
builder->Write(sectionBodyText);
builder->InsertFootnote(FootnoteType::Endnote,endnoteText);
}

