Examples


Shows how to digitally sign documents. //CreateanX.509certificatefromaPKCS#12store,whichshouldcontainaprivatekey.
SharedPtr<CertificateHolder>certificateHolder=CertificateHolder::Create(MyDir+u"morzal.pfx",u"aw");

//Createacommentanddatewhichwillbeappliedwithournewdigitalsignature.
autosignOptions=MakeObject<SignOptions>();
signOptions->set_Comments(u"Mycomment");
signOptions->set_SignTime(System::DateTime::get_Now());

//Takeanunsigneddocumentfromthelocalfilesystemviaafilestream,
//thencreateasignedcopyofitdeterminedbythefilenameoftheoutputfilestream.
{
SharedPtr<System::IO::Stream>streamIn=MakeObject<System::IO::FileStream>(MyDir+u"Document.docx",System::IO::FileMode::Open);
{
SharedPtr<System::IO::Stream>streamOut=
MakeObject<System::IO::FileStream>(ArtifactsDir+u"DigitalSignatureUtil.SignDocument.docx",System::IO::FileMode::OpenOrCreate);
DigitalSignatureUtil::Sign(streamIn,streamOut,certificateHolder,signOptions);
}
}

Examples


Shows how to sign encrypted document file. //CreateanX.509certificatefromaPKCS#12store,whichshouldcontainaprivatekey.
SharedPtr<CertificateHolder>certificateHolder=CertificateHolder::Create(MyDir+u"morzal.pfx",u"aw");

//Createacomment,date,anddecryptionpasswordwhichwillbeappliedwithournewdigitalsignature.
autosignOptions=MakeObject<SignOptions>();
signOptions->set_Comments(u"Comment");
signOptions->set_SignTime(System::DateTime::get_Now());
signOptions->set_DecryptionPassword(u"docPassword");

//Setalocalsystemfilenamefortheunsignedinputdocument,andanoutputfilenameforitsnewdigitallysignedcopy.
StringinputFileName=MyDir+u"Encrypted.docx";
StringoutputFileName=ArtifactsDir+u"DigitalSignatureUtil.DecryptionPassword.docx";

DigitalSignatureUtil::Sign(inputFileName,outputFileName,certificateHolder,signOptions);

Examples


Shows how to sign a document with a personal certificate and a signature line. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

autosignatureLineOptions=MakeObject<SignatureLineOptions>();
signatureLineOptions->set_Signer(u"vderyushev");
signatureLineOptions->set_SignerTitle(u"QA");
signatureLineOptions->set_Email(u"vderyushev@aspose.com");
signatureLineOptions->set_ShowDate(true);
signatureLineOptions->set_DefaultInstructions(false);
signatureLineOptions->set_Instructions(u"Pleasesignhere.");
signatureLineOptions->set_AllowComments(true);

SharedPtr<SignatureLine>signatureLine=builder->InsertSignatureLine(signatureLineOptions)->get_SignatureLine();
signatureLine->set_ProviderId(System::Guid::Parse(u"CF5A7BB4-8F3C-4756-9DF6-BEF7F13259A2"));

ASSERT_FALSE(signatureLine->get_IsSigned());
ASSERT_FALSE(signatureLine->get_IsValid());

doc->Save(ArtifactsDir+u"DocumentBuilder.SignatureLineProviderId.docx");

autosignOptions=MakeObject<SignOptions>();
signOptions->set_SignatureLineId(signatureLine->get_Id());
signOptions->set_ProviderId(signatureLine->get_ProviderId());
signOptions->set_Comments(u"Documentwassignedbyvderyushev");
signOptions->set_SignTime(System::DateTime::get_Now());

SharedPtr<CertificateHolder>certHolder=CertificateHolder::Create(MyDir+u"morzal.pfx",u"aw");

DigitalSignatureUtil::Sign(ArtifactsDir+u"DocumentBuilder.SignatureLineProviderId.docx",
ArtifactsDir+u"DocumentBuilder.SignatureLineProviderId.Signed.docx",certHolder,signOptions);

//Re-openoursaveddocument,andverifythatthe"IsSigned"and"IsValid"propertiesbothequal"true",
//indicatingthatthesignaturelinecontainsasignature.
doc=MakeObject<Document>(ArtifactsDir+u"DocumentBuilder.SignatureLineProviderId.Signed.docx");
autoshape=System::DynamicCast<Shape>(doc->GetChild(NodeType::Shape,0,true));
signatureLine=shape->get_SignatureLine();

ASSERT_TRUE(signatureLine->get_IsSigned());
ASSERT_TRUE(signatureLine->get_IsValid());

Examples


Shows how to add a signature line to a document, and then sign it using a digital certificate. staticvoidSign()
{
StringsigneeName=u"RonWilliams";
StringsrcDocumentPath=MyDir+u"Document.docx";
StringdstDocumentPath=ArtifactsDir+u"SignDocumentCustom.Sign.docx";
StringcertificatePath=MyDir+u"morzal.pfx";
StringcertificatePassword=u"aw";

CreateSignees();

autosigneeInfo=mSignees->Find(System::Predicate<SharedPtr<Signee>>([&](SharedPtr<Signee>c){returnc->get_Name()==signeeName;}));

if(signeeInfo!=nullptr)
{
SignDocument(srcDocumentPath,dstDocumentPath,signeeInfo,certificatePath,certificatePassword);
}
else
{
FAIL()<<"Signeedoesnotexist.";
}
}

classSignee:publicSystem::Object
{
public:
System::Guidget_PersonId()
{
returnpr_PersonId;
}

voidset_PersonId(System::Guidvalue)
{
pr_PersonId=value;
}

Stringget_Name()
{
returnpr_Name;
}

voidset_Name(Stringvalue)
{
pr_Name=value;
}

Stringget_Position()
{
returnpr_Position;
}

voidset_Position(Stringvalue)
{
pr_Position=value;
}

ArrayPtr<uint8_t>get_Image()
{
returnpr_Image;
}

voidset_Image(ArrayPtr<uint8_t>value)
{
pr_Image=value;
}

Signee(System::Guidguid,Stringname,Stringposition,ArrayPtr<uint8_t>image)
{
set_PersonId(guid);
set_Name(name);
set_Position(position);
set_Image(image);
}

private:
System::Guidpr_PersonId;
Stringpr_Name;
Stringpr_Position;
ArrayPtr<uint8_t>pr_Image;
};
staticvoidSignDocument(StringsrcDocumentPath,StringdstDocumentPath,SharedPtr<ExSignDocumentCustom::Signee>signeeInfo,StringcertificatePath,
StringcertificatePassword)
{
autodocument=MakeObject<Document>(srcDocumentPath);
autobuilder=MakeObject<DocumentBuilder>(document);

//Configureandinsertasignatureline,anobjectinthedocumentthatwilldisplayasignaturethatwesignitwith.
autosignatureLineOptions=MakeObject<SignatureLineOptions>();
signatureLineOptions->set_Signer(signeeInfo->get_Name());
signatureLineOptions->set_SignerTitle(signeeInfo->get_Position());

SharedPtr<SignatureLine>signatureLine=builder->InsertSignatureLine(signatureLineOptions)->get_SignatureLine();
signatureLine->set_Id(signeeInfo->get_PersonId());

//First,wewillsaveanunsignedversionofourdocument.
builder->get_Document()->Save(dstDocumentPath);

SharedPtr<CertificateHolder>certificateHolder=CertificateHolder::Create(certificatePath,certificatePassword);

autosignOptions=MakeObject<SignOptions>();
signOptions->set_SignatureLineId(signeeInfo->get_PersonId());
signOptions->set_SignatureLineImage(signeeInfo->get_Image());

//Overwritetheunsigneddocumentwesavedabovewithaversionsignedusingthecertificate.
DigitalSignatureUtil::Sign(dstDocumentPath,dstDocumentPath,certificateHolder,signOptions);
}

staticArrayPtr<uint8_t>ImageToByteArray(SharedPtr<System::Drawing::Image>imageIn)
{
{
automs=MakeObject<System::IO::MemoryStream>();
imageIn->Save(ms,System::Drawing::Imaging::ImageFormat::get_Png());
returnms->ToArray();
}
}

staticvoidCreateSignees()
{
mSignees=MakeObject<System::Collections::Generic::List<SharedPtr<ExSignDocumentCustom::Signee>>>();
mSignees->Add(MakeObject<ExSignDocumentCustom::Signee>(System::Guid::NewGuid(),u"RonWilliams",u"ChiefExecutiveOfficer",
ImageToByteArray(System::Drawing::Image::FromFile(ImageDir+u"Logo.jpg"))));
mSignees->Add(MakeObject<ExSignDocumentCustom::Signee>(System::Guid::NewGuid(),u"StephenMorse",u"HeadofCompliance",
ImageToByteArray(System::Drawing::Image::FromFile(ImageDir+u"Logo.jpg"))));
}

staticSharedPtr<System::Collections::Generic::List<SharedPtr<ExSignDocumentCustom::Signee>>>mSignees;

