builder->get_ParagraphFormat()->set_StyleIdentifier(StyleIdentifier::Heading1);
builder->Writeln(u"TemplateDocument");
builder->get_ParagraphFormat()->set_StyleIdentifier(StyleIdentifier::Normal);
builder->Writeln(u"Somecontenthere");

//Appendallunencrypteddocumentswiththe.docextension
//fromourlocalfilesystemdirectorytothebasedocument.
autoisDocFile=[](Stringitem)
{
returnitem.EndsWith(u".doc");
};

SharedPtr<System::Collections::Generic::List<String>>docFiles=System::IO::Directory::GetFiles(MyDir,u"*.doc")->LINQ_Where(isDocFile)->LINQ_ToList();
for(constauto&fileName:docFiles)
{
