dstDoc->AppendDocument(srcDoc,ImportFormatMode::UseDestinationStyles);
}

dstDoc->Save(ArtifactsDir+u"Document.AppendAllDocumentsInFolder.doc");

srcDoc



The document to append. 




importFormatMode



Specifies how to merge style formatting that clashes. 




importFormatOptions



Allows to specify options that affect formatting of a result document.



Examples


Shows how to manage list style clashes while appending a document. //Loadadocumentwithtextinacustomstyleandcloneit.
autosrcDoc=MakeObject<Document>(MyDir+u"Customlistnumbering.docx");
SharedPtr<Document>dstDoc=srcDoc->Clone();

//Wenowhavetwodocuments,eachwithanidenticalstylenamed"CustomStyle".
//Changethetextcolorforoneofthestylestosetitapartfromtheother.
dstDoc->get_Styles()->idx_get(u"CustomStyle")->get_Font()->set_Color(System::Drawing::Color::get_DarkRed());

//Ifthereisaclashofliststyles,applythelistformatofthesourcedocument.
//Setthe"KeepSourceNumbering"propertyto"false"tonotimportanylistnumbersintothedestinationdocument.
//Setthe"KeepSourceNumbering"propertyto"true"importallclashing
//liststylenumberingwiththesameappearancethatithadinthesourcedocument.
autooptions=MakeObject<ImportFormatOptions>();
options->set_KeepSourceNumbering(keepSourceNumbering);

//Joiningtwodocumentsthathavedifferentstylesthatsharethesamenamecausesastyleclash.
//Wecanspecifyanimportformatmodewhileappendingdocumentstoresolvethisclash.
dstDoc->AppendDocument(srcDoc,ImportFormatMode::KeepDifferentStyles,options);
dstDoc->UpdateListLabels();

dstDoc->Save(ArtifactsDir+u"DocumentBuilder.AppendDocumentAndResolveStyles.docx");

Shows how to manage list style clashes while inserting a document. autodstDoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(dstDoc);
builder->InsertBreak(BreakType::ParagraphBreak);

dstDoc->get_Lists()->Add(ListTemplate::NumberDefault);
SharedPtr<Aspose::Words::Lists::List>list=dstDoc->get_Lists()->idx_get(0);

builder->get_ListFormat()->set_List(list);

for(inti=1;i<=15;i++)
{
builder->Write(String::Format(u"ListItem{0}\n",i));
}

autoattachDoc=System::DynamicCast<Document>(dstDoc->Clone(true));

//Ifthereisaclashofliststyles,applythelistformatofthesourcedocument.
//Setthe"KeepSourceNumbering"propertyto"false"tonotimportanylistnumbersintothedestinationdocument.
//Setthe"KeepSourceNumbering"propertyto"true"importallclashing
//liststylenumberingwiththesameappearancethatithadinthesourcedocument.
autoimportOptions=MakeObject<ImportFormatOptions>();
importOptions->set_KeepSourceNumbering(keepSourceNumbering);

builder->InsertBreak(BreakType::SectionBreakNewPage);
builder->InsertDocument(attachDoc,ImportFormatMode::KeepSourceFormatting,importOptions);

dstDoc->Save(ArtifactsDir+u"DocumentBuilder.InsertDocumentAndResolveStyles.docx");

Shows how to manage list style clashes while appending a clone of a document to itself. autosrcDoc=MakeObject<Document>(MyDir+u"Listitem.docx");
autodstDoc=MakeObject<Document>(MyDir+u"Listitem.docx");

//Ifthereisaclashofliststyles,applythelistformatofthesourcedocument.
//Setthe"KeepSourceNumbering"propertyto"false"tonotimportanylistnumbersintothedestinationdocument.
//Setthe"KeepSourceNumbering"propertyto"true"importallclashing
//liststylenumberingwiththesameappearancethatithadinthesourcedocument.
autobuilder=MakeObject<DocumentBuilder>(dstDoc);
builder->MoveToDocumentEnd();
builder->InsertBreak(BreakType::SectionBreakNewPage);

autooptions=MakeObject<ImportFormatOptions>();
options->set_KeepSourceNumbering(keepSourceNumbering);
builder->InsertDocument(srcDoc,ImportFormatMode::KeepSourceFormatting,options);

dstDoc->UpdateListLabels();

Examples


Shows how to remove unused custom styles from a document. autodoc=MakeObject<Document>();

doc->get_Styles()->Add(StyleType::List,u"MyListStyle1");
doc->get_Styles()->Add(StyleType::List,u"MyListStyle2");
doc->get_Styles()->Add(StyleType::Character,u"MyParagraphStyle1");
doc->get_Styles()->Add(StyleType::Character,u"MyParagraphStyle2");

//Combinedwiththebuilt-instyles,thedocumentnowhaseightstyles.
//Acustomstylecountsas"used"whileappliedtosomepartofthedocument,
//whichmeansthatthefourstylesweaddedarecurrentlyunused.
ASSERT_EQ(8,doc->get_Styles()->get_Count());

//Applyacustomcharacterstyle,andthenacustomliststyle.Doingsowillmarkthestylesas"used".
autobuilder=MakeObject<DocumentBuilder>(doc);
builder->get_Font()->set_Style(doc->get_Styles()->idx_get(u"MyParagraphStyle1"));
builder->Writeln(u"Helloworld!");

SharedPtr<Aspose::Words::Lists::List>list=doc->get_Lists()->Add(doc->get_Styles()->idx_get(u"MyListStyle1"));
builder->get_ListFormat()->set_List(list);
builder->Writeln(u"Item1");
builder->Writeln(u"Item2");

doc->Cleanup();

ASSERT_EQ(6,doc->get_Styles()->get_Count());

//Removingeverynodethatacustomstyleisappliedtomarksitas"unused"again.
//RuntheCleanupmethodagaintoremovethem.
doc->get_FirstSection()->get_Body()->RemoveAllChildren();
doc->Cleanup();

ASSERT_EQ(4,doc->get_Styles()->get_Count());

Examples


Shows how to remove all unused custom styles from a document. autodoc=MakeObject<Document>();

doc->get_Styles()->Add(StyleType::List,u"MyListStyle1");
doc->get_Styles()->Add(StyleType::List,u"MyListStyle2");
doc->get_Styles()->Add(StyleType::Character,u"MyParagraphStyle1");
doc->get_Styles()->Add(StyleType::Character,u"MyParagraphStyle2");

//Combinedwiththebuilt-instyles,thedocumentnowhaseightstyles.
//Acustomstyleismarkedas"used"whilethereisanytextwithinthedocument
//formattedinthatstyle.Thismeansthatthe4stylesweaddedarecurrentlyunused.
ASSERT_EQ(8,doc->get_Styles()->get_Count());

//Applyacustomcharacterstyle,andthenacustomliststyle.Doingsowillmarkthemas"used".
autobuilder=MakeObject<DocumentBuilder>(doc);
builder->get_Font()->set_Style(doc->get_Styles()->idx_get(u"MyParagraphStyle1"));
builder->Writeln(u"Helloworld!");

SharedPtr<Aspose::Words::Lists::List>list=doc->get_Lists()->Add(doc->get_Styles()->idx_get(u"MyListStyle1"));
builder->get_ListFormat()->set_List(list);
builder->Writeln(u"Item1");
builder->Writeln(u"Item2");

//Now,thereisoneunusedcharacterstyleandoneunusedliststyle.
//TheCleanup()method,whenconfiguredwithaCleanupOptionsobject,cantargetunusedstylesandremovethem.
autocleanupOptions=MakeObject<CleanupOptions>();
cleanupOptions->set_UnusedLists(true);
cleanupOptions->set_UnusedStyles(true);
cleanupOptions->set_UnusedBuiltinStyles(true);

doc->Cleanup(cleanupOptions);

ASSERT_EQ(4,doc->get_Styles()->get_Count());

//Removingeverynodethatacustomstyleisappliedtomarksitas"unused"again.
//ReruntheCleanupmethodtoremovethem.
doc->get_FirstSection()->get_Body()->RemoveAllChildren();
doc->Cleanup(cleanupOptions);

ASSERT_EQ(2,doc->get_Styles()->get_Count());

The cloned document.

Examples


Shows how to deep clone a document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Write(u"Helloworld!");

//Cloningwillproduceanewdocumentwiththesamecontentsastheoriginal,
//butwithauniquecopyofeachoftheoriginaldocument'snodes.
SharedPtr<Document>clone=doc->Clone();

ASSERT_EQ(doc->get_FirstSection()->get_Body()->get_FirstParagraph()->get_Runs()->idx_get(0)->GetText(),
clone->get_FirstSection()->get_Body()->get_FirstParagraph()->get_Runs()->idx_get(0)->get_Text());
ASSERT_NE(System::ObjectExt::GetHashCode(doc->get_FirstSection()->get_Body()->get_FirstParagraph()->get_Runs()->idx_get(0)),
System::ObjectExt::GetHashCode(clone->get_FirstSection()->get_Body()->get_FirstParagraph()->get_Runs()->idx_get(0)));

Documents must not have revisions before comparison.



document



Document to compare. 




author



Initials of the author to use for revisions. 




dateTime



The date and time to use for revisions.



Examples


Shows how to compare documents. autodocOriginal=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(docOriginal);
builder->Writeln(u"Thisistheoriginaldocument.");

autodocEdited=MakeObject<Document>();
builder=MakeObject<DocumentBuilder>(docEdited);
builder->Writeln(u"Thisistheediteddocument.");

//Comparingdocumentswithrevisionswillthrowanexception.
if(docOriginal->get_Revisions()->get_Count()==0&&docEdited->get_Revisions()->get_Count()==0)
{
docOriginal->Compare(docEdited,u"authorName",System::DateTime::get_Now());
}

//Afterthecomparison,theoriginaldocumentwillgainanewrevision
//foreveryelementthatisdifferentintheediteddocument.
for(constauto&r:System::IterateOver(docOriginal->get_Revisions()))
{
std::cout<<String::Format(u"Revisiontype:{0},onanodeoftype\"{1}\"",r->get_RevisionType(),r->get_ParentNode()->get_NodeType())
<<std::endl;
std::cout<<"\tChangedtext:\""<<r->get_ParentNode()->GetText()<<"\""<<std::endl;
}

//Acceptingtheserevisionswilltransformtheoriginaldocumentintotheediteddocument.
docOriginal->get_Revisions()->AcceptAll();

ASSERT_EQ(docOriginal->GetText(),docEdited->GetText());

Examples


Shows how to filter specific types of document elements when making a comparison. //Createtheoriginaldocumentandpopulateitwithvariouskindsofelements.
autodocOriginal=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(docOriginal);

//Paragraphtextreferencedwithanendnote:
builder->Writeln(u"Helloworld!Thisisthefirstparagraph.");
builder->InsertFootnote(FootnoteType::Endnote,u"Originalendnotetext.");

//Table:
builder->StartTable();
builder->InsertCell();
builder->Write(u"Originalcell1text");
builder->InsertCell();
builder->Write(u"Originalcell2text");
builder->EndTable();

//Textbox:
SharedPtr<Shape>textBox=builder->InsertShape(ShapeType::TextBox,150,20);
builder->MoveTo(textBox->get_FirstParagraph());
builder->Write(u"Originaltextboxcontents");

//DATEfield:
builder->MoveTo(docOriginal->get_FirstSection()->get_Body()->AppendParagraph(u""));
builder->InsertField(u"DATE");

//Comment:
autonewComment=MakeObject<Comment>(docOriginal,u"JohnDoe",u"J.D.",System::DateTime::get_Now());
newComment->SetText(u"Originalcomment.");
builder->get_CurrentParagraph()->AppendChild(newComment);

//Header:
builder->MoveToHeaderFooter(HeaderFooterType::HeaderPrimary);
builder->Writeln(u"Originalheadercontents.");

//Createacloneofourdocumentandperformaquickeditoneachofthecloneddocument'selements.
autodocEdited=System::DynamicCast<Document>(docOriginal->Clone(true));
SharedPtr<Paragraph>firstParagraph=docEdited->get_FirstSection()->get_Body()->get_FirstParagraph();

firstParagraph->get_Runs()->idx_get(0)->set_Text(u"helloworld!thisisthefirstparagraph,afterediting.");
firstParagraph->get_ParagraphFormat()->set_Style(docEdited->get_Styles()->idx_get(StyleIdentifier::Heading1));
(System::DynamicCast<Footnote>(docEdited->GetChild(NodeType::Footnote,0,true)))
->get_FirstParagraph()
->get_Runs()
->idx_get(1)
->set_Text(u"Editedendnotetext.");
(System::DynamicCast<Table>(docEdited->GetChild(NodeType::Table,0,true)))
->get_FirstRow()
->get_Cells()
->idx_get(1)
->get_FirstParagraph()
->get_Runs()
->idx_get(0)
->set_Text(u"EditedCell2contents");
(System::DynamicCast<Shape>(docEdited->GetChild(NodeType::Shape,0,true)))
->get_FirstParagraph()
->get_Runs()
->idx_get(0)
->set_Text(u"Editedtextboxcontents");
(System::DynamicCast<FieldDate>(docEdited->get_Range()->get_Fields()->idx_get(0)))->set_UseLunarCalendar(true);
(System::DynamicCast<Comment>(docEdited->GetChild(NodeType::Comment,0,true)))
->get_FirstParagraph()
->get_Runs()
->idx_get(0)
->set_Text(u"Editedcomment.");
docEdited->get_FirstSection()
->get_HeadersFooters()
->idx_get(HeaderFooterType::HeaderPrimary)
->get_FirstParagraph()
->get_Runs()
->idx_get(0)
->set_Text(u"Editedheadercontents.");

//Comparingdocumentscreatesarevisionforeveryeditintheediteddocument.
//ACompareOptionsobjecthasaseriesofflagsthatcansuppressrevisions
//oneachrespectivetypeofelement,effectivelyignoringtheirchange.
autocompareOptions=MakeObject<Aspose::Words::Comparing::CompareOptions>();
compareOptions->set_IgnoreFormatting(false);
compareOptions->set_IgnoreCaseChanges(false);
compareOptions->set_IgnoreComments(false);
compareOptions->set_IgnoreTables(false);
compareOptions->set_IgnoreFields(false);
compareOptions->set_IgnoreFootnotes(false);
compareOptions->set_IgnoreTextboxes(false);
compareOptions->set_IgnoreHeadersAndFooters(false);
compareOptions->set_Target(ComparisonTargetType::New);

docOriginal->Compare(docEdited,u"JohnDoe",System::DateTime::get_Now(),compareOptions);
docOriginal->Save(ArtifactsDir+u"Document.CompareOptions.docx");

Examples


Shows how to copies styles from the template to a document via Document. autotemplate_=MakeObject<Document>(MyDir+u"Rendering.docx");
autotarget=MakeObject<Document>(MyDir+u"Document.docx");

target->CopyStylesFromTemplate(template_);

Shows how to copy styles from one document to another. //Createadocument,andthenaddstylesthatwewillcopytoanotherdocument.
autotemplate_=MakeObject<Document>();

SharedPtr<Style>style=template_->get_Styles()->Add(StyleType::Paragraph,u"TemplateStyle1");
style->get_Font()->set_Name(u"TimesNewRoman");
style->get_Font()->set_Color(System::Drawing::Color::get_Navy());

style=template_->get_Styles()->Add(StyleType::Paragraph,u"TemplateStyle2");
style->get_Font()->set_Name(u"Arial");
style->get_Font()->set_Color(System::Drawing::Color::get_DeepSkyBlue());

style=template_->get_Styles()->Add(StyleType::Paragraph,u"TemplateStyle3");
style->get_Font()->set_Name(u"CourierNew");
style->get_Font()->set_Color(System::Drawing::Color::get_RoyalBlue());

ASSERT_EQ(7,template_->get_Styles()->get_Count());

//Createadocumentwhichwewillcopythestylesto.
autotarget=MakeObject<Document>();

//Createastylewiththesamenameasastylefromthetemplatedocumentandaddittothetargetdocument.
style=target->get_Styles()->Add(StyleType::Paragraph,u"TemplateStyle3");
style->get_Font()->set_Name(u"Calibri");
style->get_Font()->set_Color(System::Drawing::Color::get_Orange());

ASSERT_EQ(5,target->get_Styles()->get_Count());

//Therearetwowaysofcallingthemethodtocopyallthestylesfromonedocumenttoanother.
//1-Passingthetemplatedocumentobject:
target->CopyStylesFromTemplate(template_);

//Copyingstylesaddsallstylesfromthetemplatedocumenttothetarget
//andoverwritesexistingstyleswiththesamename.
ASSERT_EQ(7,target->get_Styles()->get_Count());

ASSERT_EQ(u"CourierNew",target->get_Styles()->idx_get(u"TemplateStyle3")->get_Font()->get_Name());
ASSERT_EQ(System::Drawing::Color::get_RoyalBlue().ToArgb(),target->get_Styles()->idx_get(u"TemplateStyle3")->get_Font()->get_Color().ToArgb());

//2-Passingthelocalsystemfilenameofatemplatedocument:
target->CopyStylesFromTemplate(MyDir+u"Rendering.docx");

ASSERT_EQ(21,target->get_Styles()->get_Count());

