Examples


Shows how to ensure that a document contains the minimal set of nodes required for editing its contents. //AnewlycreateddocumentcontainsonechildSection,whichincludesonechildBodyandonechildParagraph.
//Wecaneditthedocumentbody'scontentsbyaddingnodessuchasRunsorinlineShapestothatparagraph.
autodoc=MakeObject<Document>();
SharedPtr<NodeCollection>nodes=doc->GetChildNodes(NodeType::Any,true);

ASSERT_EQ(NodeType::Section,nodes->idx_get(0)->get_NodeType());
ASPOSE_ASSERT_EQ(doc,nodes->idx_get(0)->get_ParentNode());

ASSERT_EQ(NodeType::Body,nodes->idx_get(1)->get_NodeType());
ASPOSE_ASSERT_EQ(nodes->idx_get(0),nodes->idx_get(1)->get_ParentNode());

ASSERT_EQ(NodeType::Paragraph,nodes->idx_get(2)->get_NodeType());
ASPOSE_ASSERT_EQ(nodes->idx_get(1),nodes->idx_get(2)->get_ParentNode());

//Thisistheminimalsetofnodesthatweneedtobeabletoeditthedocument.
//Wewillnolongerbeabletoeditthedocumentifweremoveanyofthem.
doc->RemoveAllChildren();

ASSERT_EQ(0,doc->GetChildNodes(NodeType::Any,true)->get_Count());

//Callthismethodtomakesurethatthedocumenthasatleastthosethreenodessowecanedititagain.
doc->EnsureMinimum();

ASSERT_EQ(NodeType::Section,nodes->idx_get(0)->get_NodeType());
ASSERT_EQ(NodeType::Body,nodes->idx_get(1)->get_NodeType());
ASSERT_EQ(NodeType::Paragraph,nodes->idx_get(2)->get_NodeType());

(System::DynamicCast<Paragraph>(nodes->idx_get(2)))->get_Runs()->Add(MakeObject<Run>(doc,u"Helloworld!"));

