Examples


Shows how to work with built-in document properties. autodoc=MakeObject<Document>(MyDir+u"Properties.docx");

//The"Document"objectcontainssomeofitsmetadatainitsmembers.
std::cout<<"Documentfilename:\n\t\""<<doc->get_OriginalFileName()<<"\""<<std::endl;

//Thedocumentalsostoresmetadatainitsbuilt-inproperties.
//Eachbuilt-inpropertyisamemberofthedocument's"BuiltInDocumentProperties"object.
std::cout<<"Built-inProperties:"<<std::endl;
for(constauto&docProperty:System::IterateOver(doc->get_BuiltInDocumentProperties()))
{
std::cout<<docProperty->get_Name()<<std::endl;
std::cout<<String::Format(u"\tType:\t{0}",docProperty->get_Type())<<std::endl;

//Somepropertiesmaystoremultiplevalues.
if(System::ObjectExt::Is<System::Collections::Generic::ICollection<SharedPtr<System::Object>>>(docProperty->get_Value()))
{
for(constauto&value:System::IterateOver(
System::DynamicCast_noexcept<System::Collections::Generic::ICollection<SharedPtr<System::Object>>>(docProperty->get_Value())))
{
std::cout<<"\tValue:\t\""<<value<<"\""<<std::endl;
}
}
else
{
std::cout<<"\tValue:\t\""<<docProperty->get_Value()<<"\""<<std::endl;
}
}

