builder->InsertFootnote(FootnoteType::Endnote,u"Endnote1.");
builder->Write(u"Text2.");
builder->InsertFootnote(FootnoteType::Endnote,u"Endnote2.");
builder->Write(u"Text3.");
builder->InsertFootnote(FootnoteType::Endnote,u"Endnote3.");

//Bydefault,thereferencesymbolforeachfootnoteandendnoteisitsindex
//amongallthedocument'sfootnotes/endnotes.Eachdocumentmaintainsseparatecounts
//forfootnotesandforendnotes,whichbothbeginat1.
ASSERT_EQ(1,doc->get_FootnoteOptions()->get_StartNumber());
ASSERT_EQ(1,doc->get_EndnoteOptions()->get_StartNumber());

//Wecanusethe"StartNumber"propertytogetthedocumentto
//beginafootnoteorendnotecountatadifferentnumber.
doc->get_EndnoteOptions()->set_NumberStyle(NumberStyle::Arabic);
doc->get_EndnoteOptions()->set_StartNumber(50);

doc->Save(ArtifactsDir+u"InlineStory.StartNumber.docx");

