Shows how to iterate through the children of a composite node. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Write(u"Section1");
builder->MoveToHeaderFooter(HeaderFooterType::HeaderPrimary);
builder->Write(u"Primaryheader");
builder->MoveToHeaderFooter(HeaderFooterType::FooterPrimary);
builder->Write(u"Primaryfooter");

SharedPtr<Section>section=doc->get_FirstSection();

//ASectionisacompositenodeandcancontainchildnodes,
//butonlyifthosechildnodesareofa"Body"or"HeaderFooter"nodetype.
for(constauto&node:System::IterateOver(section))
{
switch(node->get_NodeType())
{
caseNodeType::Body:{
autobody=System::DynamicCast<Body>(node);

std::cout<<"Body:"<<std::endl;
std::cout<<"\t\""<<body->GetText().Trim()<<"\""<<std::endl;
break;
}

caseNodeType::HeaderFooter:{
autoheaderFooter=System::DynamicCast<HeaderFooter>(node);

std::cout<<String::Format(u"HeaderFootertype:{0}:",headerFooter->get_HeaderFooterType())<<std::endl;
std::cout<<"\t\""<<headerFooter->GetText().Trim()<<"\""<<std::endl;
break;
}

default:{
throwSystem::Exception(u"Unexpectednodetypeinasection.");
}
}
}

Examples


Shows how set font substitution rules. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->get_Font()->set_Name(u"Arial");
builder->Writeln(u"Helloworld!");
builder->get_Font()->set_Name(u"Amethysta");
builder->Writeln(u"Thequickbrownfoxjumpsoverthelazydog.");

ArrayPtr<SharedPtr<FontSourceBase>>fontSources=FontSettings::get_DefaultInstance()->GetFontsSources();

//Thedefaultfontsourcescontainthefirstfontthatthedocumentuses.
ASSERT_EQ(1,fontSources->get_Length());
ASSERT_TRUE(fontSources[0]->GetAvailableFonts()->LINQ_Any([](SharedPtr<PhysicalFontInfo>f){returnf->get_FullFontName()==u"Arial";}));

//Thesecondfont,"Amethysta",isunavailable.
ASSERT_FALSE(fontSources[0]->GetAvailableFonts()->LINQ_Any([](SharedPtr<PhysicalFontInfo>f){returnf->get_FullFontName()==u"Amethysta";}));

//Wecanconfigureafontsubstitutiontablewhichdetermines
//whichfontsAspose.Wordswilluseassubstitutesforunavailablefonts.
//Settwosubstitutionfontsfor"Amethysta":"Arvo",and"CourierNew".
//Ifthefirstsubstituteisunavailable,Aspose.Wordsattemptstousethesecondsubstitute,andsoon.
doc->set_FontSettings(MakeObject<FontSettings>());
doc->get_FontSettings()->get_SubstitutionSettings()->get_TableSubstitution()->SetSubstitutes(u"Amethysta",
MakeArray<String>({u"Arvo",u"CourierNew"}));

//"Amethysta"isunavailable,andthesubstitutionrulestatesthatthefirstfonttouseasasubstituteis"Arvo".
ASSERT_FALSE(fontSources[0]->GetAvailableFonts()->LINQ_Any([](SharedPtr<PhysicalFontInfo>f){returnf->get_FullFontName()==u"Arvo";}));

//"Arvo"isalsounavailable,but"CourierNew"is.
ASSERT_TRUE(fontSources[0]->GetAvailableFonts()->LINQ_Any([](SharedPtr<PhysicalFontInfo>f){returnf->get_FullFontName()==u"CourierNew";}));

//Theoutputdocumentwilldisplaythetextthatusesthe"Amethysta"fontformattedwith"CourierNew".
doc->Save(ArtifactsDir+u"FontSettings.TableSubstitution.pdf");

