Shows how to change the number style of footnote/endnote reference marks. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Footnotesandendnotesareawaytoattachareferenceorasidecommenttotext
//thatdoesnotinterferewiththemainbodytext'sflow.
//Insertingafootnote/endnoteaddsasmallsuperscriptreferencesymbol
//atthemainbodytextwhereweinsertthefootnote/endnote.
//Eachfootnote/endnotealsocreatesanentry,whichconsistsofasymbolthatmatchesthereference
//symbolinthemainbodytext.Thereferencetextthatwepasstothedocumentbuilder's"InsertEndnote"method.
//Footnoteentries,bydefault,showupatthebottomofeachpagethatcontains
//theirreferencesymbols,andendnotesshowupattheendofthedocument.
builder->Write(u"Text1.");
builder->InsertFootnote(FootnoteType::Footnote,u"Footnote1.");
builder->Write(u"Text2.");
builder->InsertFootnote(FootnoteType::Footnote,u"Footnote2.");
builder->Write(u"Text3.");
builder->InsertFootnote(FootnoteType::Footnote,u"Footnote3.",u"Customfootnotereferencemark");

builder->InsertParagraph();

builder->Write(u"Text1.");
builder->InsertFootnote(FootnoteType::Endnote,u"Endnote1.");
builder->Write(u"Text2.");
builder->InsertFootnote(FootnoteType::Endnote,u"Endnote2.");
builder->Write(u"Text3.");
builder->InsertFootnote(FootnoteType::Endnote,u"Endnote3.",u"Customendnotereferencemark");

//Bydefault,thereferencesymbolforeachfootnoteandendnoteisitsindex
//amongallthedocument'sfootnotes/endnotes.Eachdocumentmaintainsseparatecounts
//forfootnotesandforendnotes.Bydefault,footnotesdisplaytheirnumbersusingArabicnumerals,
//andendnotesdisplaytheirnumbersinlowercaseRomannumerals.
ASSERT_EQ(NumberStyle::Arabic,doc->get_FootnoteOptions()->get_NumberStyle());
ASSERT_EQ(NumberStyle::LowercaseRoman,doc->get_EndnoteOptions()->get_NumberStyle());

//Wecanusethe"NumberStyle"propertytoapplycustomnumberingstylestofootnotesandendnotes.
//Thiswillnotaffectfootnotes/endnoteswithcustomreferencemarks.
doc->get_FootnoteOptions()->set_NumberStyle(NumberStyle::UppercaseRoman);
doc->get_EndnoteOptions()->set_NumberStyle(NumberStyle::UppercaseLetter);

doc->Save(ArtifactsDir+u"InlineStory.RefMarkNumberStyle.docx");

