Aspose::Words::BuildingBlocks::GlossaryDocument

Examples


Shows how to add a custom building block to a document. voidCreateAndInsert()
{
//Adocument'sglossarydocumentstoresbuildingblocks.
autodoc=MakeObject<Document>();
autoglossaryDoc=MakeObject<GlossaryDocument>();
doc->set_GlossaryDocument(glossaryDoc);

//Createabuildingblock,nameit,andthenaddittotheglossarydocument.
autoblock=MakeObject<BuildingBlock>(glossaryDoc);
block->set_Name(u"CustomBlock");

glossaryDoc->AppendChild(block);

//AllnewbuildingblockGUIDshavethesamezerovaluebydefault,andwecangivethemanewuniquevalue.
ASSERT_EQ(u"00000000-0000-0000-0000-000000000000",System::ObjectExt::ToString(block->get_Guid()));

block->set_Guid(System::Guid::NewGuid());

//Thefollowingpropertiescategorizebuildingblocks
//inthemenuwecanaccessinMicrosoftWordvia"Insert"->"QuickParts"->"BuildingBlocksOrganizer".
ASSERT_EQ(u"(EmptyCategory)",block->get_Category());
ASSERT_EQ(BuildingBlockType::None,block->get_Type());
ASSERT_EQ(BuildingBlockGallery::All,block->get_Gallery());
ASSERT_EQ(BuildingBlockBehavior::Content,block->get_Behavior());

//Beforewecanaddthisbuildingblocktoourdocument,wewillneedtogiveitsomecontents,
//whichwewilldousingadocumentvisitor.Thisvisitorwillalsosetacategory,gallery,andbehavior.
autovisitor=MakeObject<ExBuildingBlocks::BuildingBlockVisitor>(glossaryDoc);
block->Accept(visitor);

//Wecanaccesstheblockthatwejustmadefromtheglossarydocument.
SharedPtr<BuildingBlock>customBlock=glossaryDoc->GetBuildingBlock(BuildingBlockGallery::QuickParts,u"Mycustombuildingblocks",u"CustomBlock");

//Theblockitselfisasectionthatcontainsthetext.
ASSERT_EQ(String::Format(u"Textinside{0}\f",customBlock->get_Name()),customBlock->get_FirstSection()->get_Body()->get_FirstParagraph()->GetText());
ASPOSE_ASSERT_EQ(customBlock->get_FirstSection(),customBlock->get_LastSection());
std::function<void()>parseGuid=[&customBlock]()
{
System::Guid::Parse(System::ObjectExt::ToString(customBlock->get_Guid()));
};

//Now,wecaninsertitintothedocumentasanewsection.
doc->AppendChild(doc->ImportNode(customBlock->get_FirstSection(),true));

//WecanalsofinditinMicrosoftWord'sBuildingBlocksOrganizerandplaceitmanually.
doc->Save(ArtifactsDir+u"BuildingBlocks.CreateAndInsert.dotx");
}

classBuildingBlockVisitor:publicDocumentVisitor
{
public:
BuildingBlockVisitor(SharedPtr<GlossaryDocument>ownerGlossaryDoc)
{
mBuilder=MakeObject<System::Text::StringBuilder>();
mGlossaryDoc=ownerGlossaryDoc;
}

VisitorActionVisitBuildingBlockStart(SharedPtr<BuildingBlock>block)override
{
//Configurethebuildingblockasaquickpart,andaddpropertiesusedbyBuildingBlocksOrganizer.
block->set_Behavior(BuildingBlockBehavior::Paragraph);
block->set_Category(u"Mycustombuildingblocks");
block->set_Description(u"UsingthisblockintheQuickPartssectionofwordwillplaceitscontentsatthecursor.");
block->set_Gallery(BuildingBlockGallery::QuickParts);

//Addasectionwithtext.
//Insertingtheblockintothedocumentwillappendthissectionwithitschildnodesatthelocation.
autosection=MakeObject<Section>(mGlossaryDoc);
block->AppendChild(section);
block->get_FirstSection()->EnsureMinimum();

autorun=MakeObject<Run>(mGlossaryDoc,String(u"Textinside")+block->get_Name());
block->get_FirstSection()->get_Body()->get_FirstParagraph()->AppendChild(run);

returnVisitorAction::Continue;
}

VisitorActionVisitBuildingBlockEnd(SharedPtr<BuildingBlock>block)override
{
mBuilder->Append(String(u"Visited")+block->get_Name()+u"\r\n");
returnVisitorAction::Continue;
}

private:
SharedPtr<System::Text::StringBuilder>mBuilder;
SharedPtr<GlossaryDocument>mGlossaryDoc;
};

