Shows how to create a new section with a document builder. autodoc=MakeObject<Document>();

//Ablankdocumentcontainsonesectionbydefault,
//whichcontainschildnodesthatwecanedit.
ASSERT_EQ(1,doc->get_Sections()->get_Count());

//Useadocumentbuildertoaddtexttothefirstsection.
autobuilder=MakeObject<DocumentBuilder>(doc);
builder->Writeln(u"Helloworld!");

//Createasecondsectionbyinsertingasectionbreak.
builder->InsertBreak(BreakType::SectionBreakNewPage);

ASSERT_EQ(2,doc->get_Sections()->get_Count());

//Eachsectionhasitsownpagesetupsettings.
//Wecansplitthetextinthesecondsectionintotwocolumns.
//Thiswillnotaffectthetextinthefirstsection.
doc->get_LastSection()->get_PageSetup()->get_TextColumns()->SetCount(2);
builder->Writeln(u"Column1.");
builder->InsertBreak(BreakType::ColumnBreak);
builder->Writeln(u"Column2.");

ASSERT_EQ(1,doc->get_FirstSection()->get_PageSetup()->get_TextColumns()->get_Count());
ASSERT_EQ(2,doc->get_LastSection()->get_PageSetup()->get_TextColumns()->get_Count());

doc->Save(ArtifactsDir+u"Section.Create.docx");

