Aspose::Words::Markup::CustomPart

Examples


Shows how to access a document's arbitrary custom parts collection. autodoc=MakeObject<Document>(MyDir+u"CustompartsOOXMLpackage.docx");

ASSERT_EQ(2,doc->get_PackageCustomParts()->get_Count());

//Clonethesecondpart,thenaddtheclonetothecollection.
SharedPtr<CustomPart>clonedPart=doc->get_PackageCustomParts()->idx_get(1)->Clone();
doc->get_PackageCustomParts()->Add(clonedPart);

ASSERT_EQ(3,doc->get_PackageCustomParts()->get_Count());

//Enumerateoverthecollectionandprinteverypart.
{
SharedPtr<System::Collections::Generic::IEnumerator<SharedPtr<CustomPart>>>enumerator=doc->get_PackageCustomParts()->GetEnumerator();
intindex=0;
while(enumerator->MoveNext())
{
std::cout<<"Partindex"<<index<<":"<<std::endl;
std::cout<<"\tName:\t\t\t\t"<<enumerator->get_Current()->get_Name()<<std::endl;
std::cout<<"\tContenttype:\t\t"<<enumerator->get_Current()->get_ContentType()<<std::endl;
std::cout<<"\tRelationshiptype:\t"<<enumerator->get_Current()->get_RelationshipType()<<std::endl;
std::cout<<(enumerator->get_Current()->get_IsExternal()
?u"\tSourcedfromoutsidethedocument"
:String::Format(u"\tStoredwithinthedocument,length:{0}bytes",enumerator->get_Current()->get_Data()->get_Length()))
<<std::endl;
index++;
}
}

//Wecanremoveelementsfromthiscollectionindividually,orallatonce.
doc->get_PackageCustomParts()->RemoveAt(2);

ASSERT_EQ(2,doc->get_PackageCustomParts()->get_Count());

doc->get_PackageCustomParts()->Clear();

ASSERT_EQ(0,doc->get_PackageCustomParts()->get_Count());

