ASSERT_EQ(3,variables->get_Count());

//WecandisplaythevaluesofvariablesinthedocumentbodyusingDOCVARIABLEfields.
autobuilder=MakeObject<DocumentBuilder>(doc);
autofield=System::DynamicCast<FieldDocVariable>(builder->InsertField(FieldType::FieldDocVariable,true));
field->set_VariableName(u"Homeaddress");
field->Update();

ASSERT_EQ(u"123MainSt.",field->get_Result());

//Assigningvaluestoexistingkeyswillupdatethem.
variables->Add(u"Homeaddress",u"456QueenSt.");

//WewillthenhavetoupdateDOCVARIABLEfieldstoensuretheydisplayanup-to-datevalue.
ASSERT_EQ(u"123MainSt.",field->get_Result());

field->Update();

ASSERT_EQ(u"456QueenSt.",field->get_Result());

//Verifythatthedocumentvariableswithacertainnameorvalueexist.
ASSERT_TRUE(variables->Contains(u"City"));
ASSERT_TRUE(variables->LINQ_Any([](autov){returnv.get_Value()==u"London";}));

//Thecollectionofvariablesautomaticallysortsvariablesalphabeticallybyname.
ASSERT_EQ(0,variables->IndexOfKey(u"Bedrooms"));
ASSERT_EQ(1,variables->IndexOfKey(u"City"));
ASSERT_EQ(2,variables->IndexOfKey(u"Homeaddress"));

//Enumerateoverthecollectionofvariables.
{
SharedPtr<System::Collections::Generic::IEnumerator<System::Collections::Generic::KeyValuePair<String,String>>>enumerator=
doc->get_Variables()->GetEnumerator();
while(enumerator->MoveNext())
{
std::cout<<"Name:"<<enumerator->get_Current().get_Key()<<",Value:"<<enumerator->get_Current().get_Value()<<std::endl;
}
}

//Belowarethreewaysofremovingdocumentvariablesfromacollection.
//1-Byname:
variables->Remove(u"City");

ASSERT_FALSE(variables->Contains(u"City"));

//2-Byindex:
variables->RemoveAt(1);

ASSERT_FALSE(variables->Contains(u"Homeaddress"));

//3-Clearthewholecollectionatonce:
variables->Clear();

ASSERT_EQ(0,variables->get_Count());

Examples


Shows how to access a document's VBA project information. autodoc=MakeObject<Document>(MyDir+u"VBAproject.docm");

//AVBAprojectcontainsacollectionofVBAmodules.
SharedPtr<VbaProject>vbaProject=doc->get_VbaProject();
std::cout<<(vbaProject->get_IsSigned()?String::Format(u"Projectname:{0}signed;Projectcodepage:{1};Modulescount:{2}\n",
vbaProject->get_Name(),vbaProject->get_CodePage(),vbaProject->get_Modules()->LINQ_Count())
:String::Format(u"Projectname:{0}notsigned;Projectcodepage:{1};Modulescount:{2}\n",
vbaProject->get_Name(),vbaProject->get_CodePage(),vbaProject->get_Modules()->LINQ_Count()))
<<std::endl;

SharedPtr<VbaModuleCollection>vbaModules=doc->get_VbaProject()->get_Modules();

ASSERT_EQ(vbaModules->LINQ_Count(),3);

for(constauto&module_:vbaModules)
{
std::cout<<"Modulename:"<<module_->get_Name()<<";\nModulecode:\n"<<module_->get_SourceCode()<<"\n"<<std::endl;
}

//SetnewsourcecodeforVBAmodule.YoucanaccessVBAmodulesinthecollectioneitherbyindexorbyname.
vbaModules->idx_get(0)->set_SourceCode(u"YourVBAcode...");
vbaModules->idx_get(u"Module1")->set_SourceCode(u"YourVBAcode...");

//Removeamodulefromthecollection.
vbaModules->Remove(vbaModules->idx_get(2));

