//tomodifythewayinwhichthatmethodrendersthedocumentintoanimage.
autooptions=MakeObject<ImageSaveOptions>(SaveFormat::Tiff);

for(inti=0;i<doc->get_PageCount();i++)
{
//Setthe"PageSet"propertytothenumberofthefirstpagefrom
//whichtostartrenderingthedocumentfrom.
options->set_PageSet(MakeObject<PageSet>(i));

