}

Shows how to configure compression while saving a document as a JPEG. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);
builder->InsertImage(ImageDir+u"Logo.jpg");

//Createan"ImageSaveOptions"objectwhichwecanpasstothedocument's"Save"method
//tomodifythewayinwhichthatmethodrendersthedocumentintoanimage.
autoimageOptions=MakeObject<ImageSaveOptions>(SaveFormat::Jpeg);

//Setthe"JpegQuality"propertyto"10"tousestrongercompressionwhenrenderingthedocument.
//Thiswillreducethefilesizeofthedocument,buttheimagewilldisplaymoreprominentcompressionartifacts.
imageOptions->set_JpegQuality(10);

doc->Save(ArtifactsDir+u"ImageSaveOptions.JpegQuality.HighCompression.jpg",imageOptions);

ASSERT_GE(20000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"ImageSaveOptions.JpegQuality.HighCompression.jpg")->get_Length());

//Setthe"JpegQuality"propertyto"100"touseweakercompressionwhenrendingthedocument.
//Thiswillimprovethequalityoftheimageatthecostofanincreasedfilesize.
imageOptions->set_JpegQuality(100);

doc->Save(ArtifactsDir+u"ImageSaveOptions.JpegQuality.HighQuality.jpg",imageOptions);

ASSERT_LT(60000,MakeObject<System::IO::FileInfo>(ArtifactsDir+u"ImageSaveOptions.JpegQuality.HighQuality.jpg")->get_Length());

Shows how to convert a whole document to PDF with three levels in the document outline. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Insertheadingsoflevels1to5.
builder->get_ParagraphFormat()->set_StyleIdentifier(StyleIdentifier::Heading1);

ASSERT_TRUE(builder->get_ParagraphFormat()->get_IsHeading());

builder->Writeln(u"Heading1");

builder->get_ParagraphFormat()->set_StyleIdentifier(StyleIdentifier::Heading2);

builder->Writeln(u"Heading1.1");
builder->Writeln(u"Heading1.2");

builder->get_ParagraphFormat()->set_StyleIdentifier(StyleIdentifier::Heading3);

builder->Writeln(u"Heading1.2.1");
builder->Writeln(u"Heading1.2.2");

builder->get_ParagraphFormat()->set_StyleIdentifier(StyleIdentifier::Heading4);

builder->Writeln(u"Heading1.2.2.1");
builder->Writeln(u"Heading1.2.2.2");

builder->get_ParagraphFormat()->set_StyleIdentifier(StyleIdentifier::Heading5);

builder->Writeln(u"Heading1.2.2.2.1");
builder->Writeln(u"Heading1.2.2.2.2");

//Createa"PdfSaveOptions"objectthatwecanpasstothedocument's"Save"method
//tomodifyhowthatmethodconvertsthedocumentto.PDF.
autooptions=MakeObject<PdfSaveOptions>();

//TheoutputPDFdocumentwillcontainanoutline,whichisatableofcontentsthatlistsheadingsinthedocumentbody.
//Clickingonanentryinthisoutlinewilltakeustothelocationofitsrespectiveheading.
//Setthe"HeadingsOutlineLevels"propertyto"4"toexcludeallheadingswhoselevelsareabove4fromtheoutline.
options->get_OutlineOptions()->set_HeadingsOutlineLevels(4);

//Ifanoutlineentryhassubsequententriesofahigherlevelinbetweenitselfandthenextentryofthesameorlowerlevel,
//anarrowwillappeartotheleftoftheentry.Thisentryisthe"owner"ofseveralsuch"sub-entries".
//Inourdocument,theoutlineentriesfromthe5thheadinglevelaresub-entriesofthesecond4thleveloutlineentry,
//the4thand5thheadinglevelentriesaresub-entriesofthesecond3rdlevelentry,andsoon.
//Intheoutline,wecanclickonthearrowofthe"owner"entrytocollapse/expandallitssub-entries.
//Setthe"ExpandedOutlineLevels"propertyto"2"toautomaticallyexpandallheadinglevel2andloweroutlineentries
//andcollapseallleveland3andhigherentrieswhenweopenthedocument.
options->get_OutlineOptions()->set_ExpandedOutlineLevels(2);

doc->Save(ArtifactsDir+u"PdfSaveOptions.ExpandedOutlineLevels.pdf",options);

