Shows how to track revisions while editing a document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Editingadocumentusuallydoesnotcountasarevisionuntilwebegintrackingthem.
builder->Write(u"Helloworld!");

ASSERT_EQ(0,doc->get_Revisions()->get_Count());
ASSERT_FALSE(doc->get_FirstSection()->get_Body()->get_Paragraphs()->idx_get(0)->get_Runs()->idx_get(0)->get_IsInsertRevision());

doc->StartTrackRevisions(u"JohnDoe");

builder->Write(u"Helloagain!");

ASSERT_EQ(1,doc->get_Revisions()->get_Count());
ASSERT_TRUE(doc->get_FirstSection()->get_Body()->get_Paragraphs()->idx_get(0)->get_Runs()->idx_get(1)->get_IsInsertRevision());
ASSERT_EQ(u"JohnDoe",doc->get_Revisions()->idx_get(0)->get_Author());
ASSERT_LE((System::DateTime::get_Now()-doc->get_Revisions()->idx_get(0)->get_DateTime()).get_Milliseconds(),10);

//Stoptrackingrevisionstonotcountanyfutureeditsasrevisions.
doc->StopTrackRevisions();
builder->Write(u"Helloagain!");

ASSERT_EQ(1,doc->get_Revisions()->get_Count());
ASSERT_FALSE(doc->get_FirstSection()->get_Body()->get_Paragraphs()->idx_get(0)->get_Runs()->idx_get(2)->get_IsInsertRevision());

//Creatingrevisionsgivesthemadateandtimeoftheoperation.
//WecandisablethisbypassingDateTime.MinValuewhenwestarttrackingrevisions.
doc->StartTrackRevisions(u"JohnDoe",System::DateTime::MinValue);
builder->Write(u"Helloagain!");

ASSERT_EQ(2,doc->get_Revisions()->get_Count());
ASSERT_EQ(u"JohnDoe",doc->get_Revisions()->idx_get(1)->get_Author());
ASSERT_EQ(System::DateTime::MinValue,doc->get_Revisions()->idx_get(1)->get_DateTime());

//Wecanaccept/rejecttheserevisionsprogrammatically
//bycallingmethodssuchasDocument.AcceptAllRevisions,oreachrevision'sAcceptmethod.
//InMicrosoftWord,wecanprocessthemmanuallyvia"Review"->"Changes".
doc->Save(ArtifactsDir+u"Document.StartTrackRevisions.docx");

author



Initials of the author to use for revisions. 




dateTime



The date and time to use for revisions.




