Aspose::Words::Lists::ListFormat

Examples


Shows how to work with list levels. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

ASSERT_FALSE(builder->get_ListFormat()->get_IsListItem());

//Alistallowsustoorganizeanddecoratesetsofparagraphswithprefixsymbolsandindents.
//Wecancreatenestedlistsbyincreasingtheindentlevel.
//Wecanbeginandendalistbyusingadocumentbuilder's"ListFormat"property.
//Eachparagraphthatweaddbetweenalist'sstartandtheendwillbecomeaniteminthelist.
//Belowaretwotypesofliststhatwecancreateusingadocumentbuilder.
//1-Anumberedlist:
//Numberedlistscreatealogicalorderfortheirparagraphsbynumberingeachitem.
builder->get_ListFormat()->set_List(doc->get_Lists()->Add(ListTemplate::NumberDefault));

ASSERT_TRUE(builder->get_ListFormat()->get_IsListItem());

//Bysettingthe"ListLevelNumber"property,wecanincreasethelistlevel
//tobeginaself-containedsub-listatthecurrentlistitem.
//TheMicrosoftWordlisttemplatecalled"NumberDefault"usesnumberstocreatelistlevelsforthefirstlistlevel.
//DeeperlistlevelsuselettersandlowercaseRomannumerals.
for(inti=0;i<9;i++)
{
builder->get_ListFormat()->set_ListLevelNumber(i);
builder->Writeln(String(u"Level")+i);
}

//2-Abulletedlist:
//Thislistwillapplyanindentandabulletsymbol("")beforeeachparagraph.
//Deeperlevelsofthislistwillusedifferentsymbols,suchas""and"".
builder->get_ListFormat()->set_List(doc->get_Lists()->Add(ListTemplate::BulletDefault));

for(inti=0;i<9;i++)
{
builder->get_ListFormat()->set_ListLevelNumber(i);
builder->Writeln(String(u"Level")+i);
}

//Wecandisablelistformattingtonotformatanysubsequentparagraphsaslistsbyun-settingthe"List"flag.
builder->get_ListFormat()->set_List(nullptr);

ASSERT_FALSE(builder->get_ListFormat()->get_IsListItem());

doc->Save(ArtifactsDir+u"Lists.SpecifyListLevel.docx");

Examples


Shows how customize node changing with a callback. voidFontChangeViaCallback()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Setthenodechangingcallbacktocustomimplementation,
//thenadd/removenodestogetittogeneratealog.
autocallback=MakeObject<ExDocument::HandleNodeChangingFontChanger>();
doc->set_NodeChangingCallback(callback);

builder->Writeln(u"Helloworld!");
builder->Writeln(u"Helloagain!");
builder->InsertField(u"HYPERLINK\"https://www.google.com/\"");
builder->InsertShape(ShapeType::Rectangle,300,300);

doc->get_Range()->get_Fields()->idx_get(0)->Remove();

std::cout<<callback->GetLog()<<std::endl;
}

classHandleNodeChangingFontChanger:publicINodeChangingCallback
{
public:
StringGetLog()
{
returnmLog->ToString();
}

HandleNodeChangingFontChanger():mLog(MakeObject<System::Text::StringBuilder>())
{
}

private:
SharedPtr<System::Text::StringBuilder>mLog;

voidNodeInserted(SharedPtr<NodeChangingArgs>args)override
{
mLog->AppendLine(String::Format(u"\tType:\t{0}",args->get_Node()->get_NodeType()));
mLog->AppendLine(String::Format(u"\tHash:\t{0}",System::ObjectExt::GetHashCode(args->get_Node())));

if(args->get_Node()->get_NodeType()==NodeType::Run)
{
SharedPtr<Aspose::Words::Font>font=(System::DynamicCast<Run>(args->get_Node()))->get_Font();
mLog->Append(String::Format(u"\tFont:\tChangedfrom\"{0}\"{1}pt",font->get_Name(),font->get_Size()));

font->set_Size(24);
font->set_Name(u"Arial");

mLog->AppendLine(String::Format(u"to\"{0}\"{1}pt",font->get_Name(),font->get_Size()));
mLog->AppendLine(String::Format(u"\tContents:\n\t\t\"{0}\"",args->get_Node()->GetText()));
}
}

voidNodeInserting(SharedPtr<NodeChangingArgs>args)override
{
mLog->AppendLine(String::Format(u"\n{0:dd/MM/yyyyHH:mm:ss:fff}\tNodeinsertion:",System::DateTime::get_Now()));
}

voidNodeRemoved(SharedPtr<NodeChangingArgs>args)override
{
mLog->AppendLine(String::Format(u"\tType:\t{0}",args->get_Node()->get_NodeType()));
mLog->AppendLine(String::Format(u"\tHashcode:\t{0}",System::ObjectExt::GetHashCode(args->get_Node())));
}

voidNodeRemoving(SharedPtr<NodeChangingArgs>args)override
{
mLog->AppendLine(String::Format(u"\n{0:dd/MM/yyyyHH:mm:ss:fff}\tNoderemoval:",System::DateTime::get_Now()));
}
};

