Examples


Shows how to create bulleted and numbered lists. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->Writeln(u"Aspose.Wordsmainadvantagesare:");

//Alistallowsustoorganizeanddecoratesetsofparagraphswithprefixsymbolsandindents.
//Wecancreatenestedlistsbyincreasingtheindentlevel.
//Wecanbeginandendalistbyusingadocumentbuilder's"ListFormat"property.
//Eachparagraphthatweaddbetweenalist'sstartandtheendwillbecomeaniteminthelist.
//Belowaretwotypesofliststhatwecancreatewithadocumentbuilder.
//1-Abulletedlist:
//Thislistwillapplyanindentandabulletsymbol("")beforeeachparagraph.
builder->get_ListFormat()->ApplyBulletDefault();
builder->Writeln(u"Greatperformance");
builder->Writeln(u"Highreliability");
builder->Writeln(u"Qualitycodeandworking");
builder->Writeln(u"Widevarietyoffeatures");
builder->Writeln(u"EasytounderstandAPI");

//Endthebulletedlist.
builder->get_ListFormat()->RemoveNumbers();

builder->InsertBreak(BreakType::ParagraphBreak);
builder->Writeln(u"Aspose.Wordsallows:");

//2-Anumberedlist:
//Numberedlistscreatealogicalorderfortheirparagraphsbynumberingeachitem.
builder->get_ListFormat()->ApplyNumberDefault();

//Thisparagraphisthefirstitem.Thefirstitemofanumberedlistwillhavea"1."asitslistitemsymbol.
builder->Writeln(u"Openingdocumentsfromdifferentformats:");

ASSERT_EQ(0,builder->get_ListFormat()->get_ListLevelNumber());

//Callthe"ListIndent"methodtoincreasethecurrentlistlevel,
//whichwillstartanewself-containedlist,withadeeperindent,atthecurrentitemofthefirstlistlevel.
builder->get_ListFormat()->ListIndent();

ASSERT_EQ(1,builder->get_ListFormat()->get_ListLevelNumber());

//Thesearethefirstthreelistitemsofthesecondlistlevel,whichwillmaintainacount
//independentofthecountofthefirstlistlevel.Accordingtothecurrentlistformat,
//theywillhavesymbolsof"a.","b.",and"c.".
builder->Writeln(u"DOC");
builder->Writeln(u"PDF");
builder->Writeln(u"HTML");

//Callthe"ListOutdent"methodtoreturntothepreviouslistlevel.
builder->get_ListFormat()->ListOutdent();

ASSERT_EQ(0,builder->get_ListFormat()->get_ListLevelNumber());

//Thesetwoparagraphswillcontinuethecountofthefirstlistlevel.
//Theseitemswillhavesymbolsof"2.",and"3."
builder->Writeln(u"Processingdocuments");
builder->Writeln(u"Savingdocumentsindifferentformats:");

//Ifweincreasethelistleveltoalevelthatwehaveaddeditemstopreviously,
//thenestedlistwillbeseparatefromtheprevious,anditsnumberingwillstartfromthebeginning.
//Theselistitemswillhavesymbolsof"a.","b.","c.","d.",and"e".
builder->get_ListFormat()->ListIndent();
builder->Writeln(u"DOC");
builder->Writeln(u"PDF");
builder->Writeln(u"HTML");
builder->Writeln(u"MHTML");
builder->Writeln(u"Plaintext");

//Outdentthelistlevelagain.
builder->get_ListFormat()->ListOutdent();
builder->Writeln(u"Doingmanyotherthings!");

//Endthenumberedlist.
builder->get_ListFormat()->RemoveNumbers();

doc->Save(ArtifactsDir+u"Lists.ApplyDefaultBulletsAndNumbers.docx");

