Shows how to build a table with custom borders. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

builder->StartTable();

//Settingtableformattingoptionsforadocumentbuilder
//willapplythemtoeveryrowandcellthatweaddwithit.
builder->get_ParagraphFormat()->set_Alignment(ParagraphAlignment::Center);

builder->get_CellFormat()->ClearFormatting();
builder->get_CellFormat()->set_Width(150);
builder->get_CellFormat()->set_VerticalAlignment(CellVerticalAlignment::Center);
builder->get_CellFormat()->get_Shading()->set_BackgroundPatternColor(System::Drawing::Color::get_GreenYellow());
builder->get_CellFormat()->set_WrapText(false);
builder->get_CellFormat()->set_FitText(true);

builder->get_RowFormat()->ClearFormatting();
builder->get_RowFormat()->set_HeightRule(HeightRule::Exactly);
builder->get_RowFormat()->set_Height(50);
builder->get_RowFormat()->get_Borders()->set_LineStyle(LineStyle::Engrave3D);
builder->get_RowFormat()->get_Borders()->set_Color(System::Drawing::Color::get_Orange());

builder->InsertCell();
builder->Write(u"Row1,Col1");

builder->InsertCell();
builder->Write(u"Row1,Col2");
builder->EndRow();

//Changingtheformattingwillapplyittothecurrentcell,
//andanynewcellsthatwecreatewiththebuilderafterward.
//Thiswillnotaffectthecellsthatwehaveaddedpreviously.
builder->get_CellFormat()->get_Shading()->ClearFormatting();

builder->InsertCell();
builder->Write(u"Row2,Col1");

builder->InsertCell();
builder->Write(u"Row2,Col2");

builder->EndRow();

//Increaserowheighttofittheverticaltext.
builder->InsertCell();
builder->get_RowFormat()->set_Height(150);
builder->get_CellFormat()->set_Orientation(TextOrientation::Upward);
builder->Write(u"Row3,Col1");

builder->InsertCell();
builder->get_CellFormat()->set_Orientation(TextOrientation::Downward);
builder->Write(u"Row3,Col2");

builder->EndRow();
builder->EndTable();

doc->Save(ArtifactsDir+u"DocumentBuilder.InsertTable.docx");

Shows how to build a formatted 2x2 table. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Table>table=builder->StartTable();
builder->InsertCell();
builder->get_CellFormat()->set_VerticalAlignment(CellVerticalAlignment::Center);
builder->Write(u"Row1,cell1.");
builder->InsertCell();
builder->Write(u"Row1,cell2.");
builder->EndRow();

//Whilebuildingthetable,thedocumentbuilderwillapplyitscurrentRowFormat/CellFormatpropertyvalues
//tothecurrentrow/cellthatitscursorisinandanynewrows/cellsasitcreatesthem.
ASSERT_EQ(CellVerticalAlignment::Center,table->get_Rows()->idx_get(0)->get_Cells()->idx_get(0)->get_CellFormat()->get_VerticalAlignment());
ASSERT_EQ(CellVerticalAlignment::Center,table->get_Rows()->idx_get(0)->get_Cells()->idx_get(1)->get_CellFormat()->get_VerticalAlignment());

builder->InsertCell();
builder->get_RowFormat()->set_Height(100);
builder->get_RowFormat()->set_HeightRule(HeightRule::Exactly);
builder->get_CellFormat()->set_Orientation(TextOrientation::Upward);
builder->Write(u"Row2,cell1.");
builder->InsertCell();
builder->get_CellFormat()->set_Orientation(TextOrientation::Downward);
builder->Write(u"Row2,cell2.");
builder->EndRow();
builder->EndTable();

//Previouslyaddedrowsandcellsarenotretroactivelyaffectedbychangestothebuilder'sformatting.
ASPOSE_ASSERT_EQ(0,table->get_Rows()->idx_get(0)->get_RowFormat()->get_Height());
ASSERT_EQ(HeightRule::Auto,table->get_Rows()->idx_get(0)->get_RowFormat()->get_HeightRule());
ASPOSE_ASSERT_EQ(100,table->get_Rows()->idx_get(1)->get_RowFormat()->get_Height());
ASSERT_EQ(HeightRule::Exactly,table->get_Rows()->idx_get(1)->get_RowFormat()->get_HeightRule());
ASSERT_EQ(TextOrientation::Upward,table->get_Rows()->idx_get(1)->get_Cells()->idx_get(0)->get_CellFormat()->get_Orientation());
ASSERT_EQ(TextOrientation::Downward,table->get_Rows()->idx_get(1)->get_Cells()->idx_get(1)->get_CellFormat()->get_Orientation());

doc->Save(ArtifactsDir+u"DocumentBuilder.BuildTable.docx");

Shows how to format rows with a document builder. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Table>table=builder->StartTable();
builder->InsertCell();
builder->Write(u"Row1,cell1.");

//Startasecondrow,andthenconfigureitsheight.Thebuilderwillapplythesesettingsto
//itscurrentrow,aswellasanynewrowsitcreatesafterwards.
builder->EndRow();

SharedPtr<RowFormat>rowFormat=builder->get_RowFormat();
rowFormat->set_Height(100);
rowFormat->set_HeightRule(HeightRule::Exactly);

builder->InsertCell();
builder->Write(u"Row2,cell1.");
builder->EndTable();

//Thefirstrowwasunaffectedbythepaddingreconfigurationandstillholdsthedefaultvalues.
ASPOSE_ASSERT_EQ(0.0,table->get_Rows()->idx_get(0)->get_RowFormat()->get_Height());
ASSERT_EQ(HeightRule::Auto,table->get_Rows()->idx_get(0)->get_RowFormat()->get_HeightRule());

ASPOSE_ASSERT_EQ(100.0,table->get_Rows()->idx_get(1)->get_RowFormat()->get_Height());
ASSERT_EQ(HeightRule::Exactly,table->get_Rows()->idx_get(1)->get_RowFormat()->get_HeightRule());

doc->Save(ArtifactsDir+u"DocumentBuilder.SetRowFormatting.docx");

