Shows how to insert a linked image into a document. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

StringimageFileName=ImageDir+u"WindowsMetaFile.wmf";

//Belowaretwowaysofapplyinganimagetoashapesothatitcandisplayit.
//1-Settheshapetocontaintheimage.
autoshape=MakeObject<Shape>(builder->get_Document(),ShapeType::Image);
shape->set_WrapType(WrapType::Inline);
shape->get_ImageData()->SetImage(imageFileName);

builder->InsertNode(shape);

doc->Save(ArtifactsDir+u"Image.CreateLinkedImage.Embedded.docx");

//Everyimagethatwestoreinshapewillincreasethesizeofourdocument.
ASSERT_TRUE(70000<MakeObject<System::IO::FileInfo>(ArtifactsDir+u"Image.CreateLinkedImage.Embedded.docx")->get_Length());

doc->get_FirstSection()->get_Body()->get_FirstParagraph()->RemoveAllChildren();

//2-Settheshapetolinktoanimagefileinthelocalfilesystem.
shape=MakeObject<Shape>(builder->get_Document(),ShapeType::Image);
shape->set_WrapType(WrapType::Inline);
shape->get_ImageData()->set_SourceFullName(imageFileName);

builder->InsertNode(shape);
doc->Save(ArtifactsDir+u"Image.CreateLinkedImage.Linked.docx");

//Linkingtoimageswillsavespaceandresultinasmallerdocument.
//However,thedocumentcanonlydisplaytheimagecorrectlywhile
//theimagefileispresentatthelocationthattheshape's"SourceFullName"propertypointsto.
ASSERT_TRUE(10000>MakeObject<System::IO::FileInfo>(ArtifactsDir+u"Image.CreateLinkedImage.Linked.docx")->get_Length());

