Shows how to insert an online video into a document with a custom thumbnail. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

StringvideoUrl=u"https://vimeo.com/52477838";
StringvideoEmbedCode=String(u"<iframesrc=\"https://player.vimeo.com/video/52477838\"width=\"640\"height=\"360\"frameborder=\"0\"")+
u"title=\"Aspose\"webkitallowfullscreenmozallowfullscreenallowfullscreen></iframe>";

ArrayPtr<uint8_t>thumbnailImageBytes=System::IO::File::ReadAllBytes(ImageDir+u"Logo.jpg");

{
autostream=MakeObject<System::IO::MemoryStream>(thumbnailImageBytes);
{
SharedPtr<System::Drawing::Image>image=System::Drawing::Image::FromStream(stream);
//Belowaretwowaysofcreatingashapewithacustomthumbnail,whichlinkstoanonlinevideo
//thatwillplaywhenweclickontheshapeinMicrosoftWord.
//1-Insertaninlineshapeatthebuilder'snodeinsertioncursor:
builder->InsertOnlineVideo(videoUrl,videoEmbedCode,thumbnailImageBytes,image->get_Width(),image->get_Height());

builder->InsertBreak(BreakType::PageBreak);

//2-Insertafloatingshape:
doubleleft=builder->get_PageSetup()->get_RightMargin()-image->get_Width();
doubletop=builder->get_PageSetup()->get_BottomMargin()-image->get_Height();

builder->InsertOnlineVideo(videoUrl,videoEmbedCode,thumbnailImageBytes,RelativeHorizontalPosition::RightMargin,left,
RelativeVerticalPosition::BottomMargin,top,image->get_Width(),image->get_Height(),WrapType::Square);
}
}

doc->Save(ArtifactsDir+u"DocumentBuilder.InsertOnlineVideoCustomThumbnail.docx");

