Shows how to create headers and footers in a document using DocumentBuilder. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

//Specifythatwewantdifferentheadersandfootersforfirst,evenandoddpages.
builder->get_PageSetup()->set_DifferentFirstPageHeaderFooter(true);
builder->get_PageSetup()->set_OddAndEvenPagesHeaderFooter(true);

//Createtheheaders,thenaddthreepagestothedocumenttodisplayeachheadertype.
builder->MoveToHeaderFooter(HeaderFooterType::HeaderFirst);
builder->Write(u"Headerforthefirstpage");
builder->MoveToHeaderFooter(HeaderFooterType::HeaderEven);
builder->Write(u"Headerforevenpages");
builder->MoveToHeaderFooter(HeaderFooterType::HeaderPrimary);
builder->Write(u"Headerforallotherpages");

builder->MoveToSection(0);
builder->Writeln(u"Page1");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page2");
builder->InsertBreak(BreakType::PageBreak);
builder->Writeln(u"Page3");

doc->Save(ArtifactsDir+u"DocumentBuilder.HeadersAndFooters.docx");

