Shows how to hide chart axes. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Shape>shape=builder->InsertChart(ChartType::Line,500,300);
SharedPtr<Chart>chart=shape->get_Chart();

//Clearthechart'sdemodataseriestostartwithacleanchart.
chart->get_Series()->Clear();

//AddacustomserieswithcategoriesfortheX-axis,andrespectivedecimalvaluesfortheY-axis.
chart->get_Series()->Add(u"AWSeries1",MakeArray<String>({u"Item1",u"Item2",u"Item3",u"Item4",u"Item5"}),
MakeArray<double>({1.2,0.3,2.1,2.9,4.2}));

//Hidethechartaxestosimplifytheappearanceofthechart.
chart->get_AxisX()->set_Hidden(true);
chart->get_AxisY()->set_Hidden(true);

doc->Save(ArtifactsDir+u"Charts.HideChartAxis.docx");

