Shows how to work with data labels of a bubble chart. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Chart>chart=builder->InsertChart(ChartType::Bubble,500,300)->get_Chart();

//Clearthechart'sdemodataseriestostartwithacleanchart.
chart->get_Series()->Clear();

//AddacustomserieswithX/Ycoordinatesanddiameterofeachofthebubbles.
SharedPtr<ChartSeries>series=chart->get_Series()->Add(u"AsposeTestSeries",MakeArray<double>({2.9,3.5,1.1,4.0,4.0}),
MakeArray<double>({1.9,8.5,2.1,6.0,1.5}),MakeArray<double>({9.0,4.5,2.5,8.0,5.0}));

//Enabledatalabels,andthenmodifytheirappearance.
series->set_HasDataLabels(true);
SharedPtr<ChartDataLabelCollection>dataLabels=series->get_DataLabels();
dataLabels->set_ShowBubbleSize(true);
dataLabels->set_ShowCategoryName(true);
dataLabels->set_ShowSeriesName(true);
dataLabels->set_Separator(u"&");

doc->Save(ArtifactsDir+u"Charts.DataLabelsBubbleChart.docx");

Shows how to work with data labels of a pie chart. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Chart>chart=builder->InsertChart(ChartType::Pie,500,300)->get_Chart();

//Clearthechart'sdemodataseriestostartwithacleanchart.
chart->get_Series()->Clear();

//Insertacustomchartserieswithacategorynameforeachofthesectors,andtheirfrequencytable.
SharedPtr<ChartSeries>series=
chart->get_Series()->Add(u"AsposeTestSeries",MakeArray<String>({u"Word",u"PDF",u"Excel"}),MakeArray<double>({2.7,3.2,0.8}));

//Enabledatalabelsthatwilldisplaybothpercentageandfrequencyofeachsector,andmodifytheirappearance.
series->set_HasDataLabels(true);
SharedPtr<ChartDataLabelCollection>dataLabels=series->get_DataLabels();
dataLabels->set_ShowLeaderLines(true);
dataLabels->set_ShowLegendKey(true);
dataLabels->set_ShowPercentage(true);
dataLabels->set_ShowValue(true);
dataLabels->set_Separator(u";");

doc->Save(ArtifactsDir+u"Charts.DataLabelsPieChart.docx");

