Shows how to apply labels to data points in a line chart. voidDataLabels()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Shape>chartShape=builder->InsertChart(ChartType::Line,400,300);
SharedPtr<Chart>chart=chartShape->get_Chart();

ASSERT_EQ(3,chart->get_Series()->get_Count());
ASSERT_EQ(u"Series1",chart->get_Series()->idx_get(0)->get_Name());
ASSERT_EQ(u"Series2",chart->get_Series()->idx_get(1)->get_Name());
ASSERT_EQ(u"Series3",chart->get_Series()->idx_get(2)->get_Name());

//Applydatalabelstoeveryseriesinthechart.
//Theselabelswillappearnexttoeachdatapointinthegraphanddisplayitsvalue.
for(constauto&series:System::IterateOver(chart->get_Series()))
{
ApplyDataLabels(series,4,u"000.0",u",");
ASSERT_EQ(4,series->get_DataLabels()->get_Count());
}

//Changetheseparatorstringforeverydatalabelinaseries.
{
SharedPtr<System::Collections::Generic::IEnumerator<SharedPtr<ChartDataLabel>>>enumerator=
chart->get_Series()->idx_get(0)->get_DataLabels()->GetEnumerator();
while(enumerator->MoveNext())
{
ASSERT_EQ(u",",enumerator->get_Current()->get_Separator());
enumerator->get_Current()->set_Separator(u"&");
}
}

//Foracleanerlookinggraph,wecanremovedatalabelsindividually.
chart->get_Series()->idx_get(1)->get_DataLabels()->idx_get(2)->ClearFormat();

//Wecanalsostripanentireseriesofitsdatalabelsatonce.
chart->get_Series()->idx_get(2)->get_DataLabels()->ClearFormat();

doc->Save(ArtifactsDir+u"Charts.DataLabels.docx");
}

staticvoidApplyDataLabels(SharedPtr<ChartSeries>series,intlabelsCount,StringnumberFormat,Stringseparator)
{
for(inti=0;i<labelsCount;i++)
{
series->set_HasDataLabels(true);

