Examples


Shows how to move the slices of a pie chart away from the center. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Shape>shape=builder->InsertChart(ChartType::Pie,500,350);
SharedPtr<Chart>chart=shape->get_Chart();

ASSERT_EQ(1,chart->get_Series()->get_Count());
ASSERT_EQ(u"Sales",chart->get_Series()->idx_get(0)->get_Name());

//"Slices"ofapiechartmaybemovedawayfromthecenterbyadistanceviatherespectivedatapoint'sExplosionattribute.
//Addadatapointtothefirstportionofthepiechartandmoveitawayfromthecenterby10points.
//Aspose.Wordscreatedatapointsautomaticallyifthemdoesnotexist.
SharedPtr<ChartDataPoint>dataPoint=chart->get_Series()->idx_get(0)->get_DataPoints()->idx_get(0);
dataPoint->set_Explosion(10);

//Displacethesecondportionbyagreaterdistance.
dataPoint=chart->get_Series()->idx_get(0)->get_DataPoints()->idx_get(1);
dataPoint->set_Explosion(40);

doc->Save(ArtifactsDir+u"Charts.PieChartExplosion.docx");

Examples


Sows how to set series color. autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Shape>shape=builder->InsertChart(ChartType::Column,432,252);

SharedPtr<Chart>chart=shape->get_Chart();
SharedPtr<ChartSeriesCollection>seriesColl=chart->get_Series();

//Deletedefaultgeneratedseries.
seriesColl->Clear();

//Createcategorynamesarray.
autocategories=MakeArray<String>({u"Category1",u"Category2"});

//Addingnewseries.Valueandcategoryarraysmustbethesamesize.
SharedPtr<ChartSeries>series1=seriesColl->Add(u"Series1",categories,MakeArray<double>({1,2}));
SharedPtr<ChartSeries>series2=seriesColl->Add(u"Series2",categories,MakeArray<double>({3,4}));
SharedPtr<ChartSeries>series3=seriesColl->Add(u"Series3",categories,MakeArray<double>({5,6}));

//Setseriescolor.
series1->get_Format()->get_Fill()->set_ForeColor(System::Drawing::Color::get_Red());
series2->get_Format()->get_Fill()->set_ForeColor(System::Drawing::Color::get_Yellow());
series3->get_Format()->get_Fill()->set_ForeColor(System::Drawing::Color::get_Blue());

doc->Save(ArtifactsDir+u"Charts.SeriesColor.docx");

Examples


Shows how to work with data points on a line chart. voidChartDataPoint_()
{
autodoc=MakeObject<Document>();
autobuilder=MakeObject<DocumentBuilder>(doc);

SharedPtr<Shape>shape=builder->InsertChart(ChartType::Line,500,350);
SharedPtr<Chart>chart=shape->get_Chart();

ASSERT_EQ(3,chart->get_Series()->get_Count());
ASSERT_EQ(u"Series1",chart->get_Series()->idx_get(0)->get_Name());
ASSERT_EQ(u"Series2",chart->get_Series()->idx_get(1)->get_Name());
ASSERT_EQ(u"Series3",chart->get_Series()->idx_get(2)->get_Name());

//Emphasizethechart'sdatapointsbymakingthemappearasdiamondshapes.
for(constauto&series:System::IterateOver(chart->get_Series()))
{
ApplyDataPoints(series,4,MarkerSymbol::Diamond,15);
}

//Smoothoutthelinethatrepresentsthefirstdataseries.
chart->get_Series()->idx_get(0)->set_Smooth(true);

//Verifythatdatapointsforthefirstserieswillnotinverttheircolorsifthevalueisnegative.
{
SharedPtr<System::Collections::Generic::IEnumerator<SharedPtr<ChartDataPoint>>>enumerator=
chart->get_Series()->idx_get(0)->get_DataPoints()->GetEnumerator();
while(enumerator->MoveNext())
{
ASSERT_FALSE(enumerator->get_Current()->get_InvertIfNegative());
}
}

//Foracleanerlookinggraph,wecanclearformatindividually.
chart->get_Series()->idx_get(1)->get_DataPoints()->idx_get(2)->ClearFormat();

//Wecanalsostripanentireseriesofdatapointsatonce.
chart->get_Series()->idx_get(2)->get_DataPoints()->ClearFormat();

doc->Save(ArtifactsDir+u"Charts.ChartDataPoint.docx");
}

staticvoidApplyDataPoints(SharedPtr<ChartSeries>series,intdataPointsCount,MarkerSymbolmarkerSymbol,intdataPointSize)
{
for(inti=0;i<dataPointsCount;i++)
{
SharedPtr<ChartDataPoint>point=series->get_DataPoints()->idx_get(i);
point->get_Marker()->set_Symbol(markerSymbol);
point->get_Marker()->set_Size(dataPointSize);

ASSERT_EQ(i,point->get_Index());
}
}

